// Command line execution:
// java MainMethodExample arg1 arg2 arg3

public class StaticExample {
    static int staticVariable = 10;    // Class variable
    int instanceVariable = 20;         // Instance variable

    // Static method
    public static void staticMethod() {
        System.out.println("Static method called");
        System.out.println("Static variable: " + staticVariable);

        // Cannot access instance variable directly
        // System.out.println(instanceVariable); // Compilation Error

        // Cannot call non-static method directly
        // instanceMethod(); // Compilation Error
    }

    // Non-static method
    public void instanceMethod() {
        System.out.println("Instance method called");
        System.out.println("Static variable: " + staticVariable);     // Can access
        System.out.println("Instance variable: " + instanceVariable); // Can access

        staticMethod(); // Can call static method
    }

    public static void main(String[] args) {
        // Calling static method
        StaticExample.staticMethod();  // Using class name
        staticMethod();                // Direct call (same class)

        // Calling non-static method
        StaticExample obj = new StaticExample();
        obj.instanceMethod();

        // Accessing static variable
        System.out.println(StaticExample.staticVariable);
    }
}

