public class ExceptionHandlingExample {
    public static void main(String[] args) {
        // Basic try-catch
        try {
            int result = 10 / 0;  // ArithmeticException
        } catch (ArithmeticException e) {
            System.out.println("Cannot divide by zero: " + e.getMessage());
        }

        // Multiple catch blocks
        try {
            int[] arr = {1, 2, 3};
            System.out.println(arr[5]);  // ArrayIndexOutOfBoundsException
        } catch (ArrayIndexOutOfBoundsException e) {
            System.out.println("Array index out of bounds");
        } catch (Exception e) {
            System.out.println("General exception: " + e.getMessage());
        }

        // try-catch-finally
        try {
            String str = null;
            System.out.println(str.length());  // NullPointerException
        } catch (NullPointerException e) {
            System.out.println("Null pointer exception occurred");
        } finally {
            System.out.println("Finally block always executes");
        }

        // try-with-resources (Java 7+)
        try (Scanner scanner = new Scanner(System.in)) {
            // Resource is automatically closed
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    // Method throwing checked exception
    public void readFile() throws IOException {
        FileReader file = new FileReader("example.txt");
        // File operations
        file.close();
    }
}

import java.util.*;

public class CollectionsBasics {
    public static void main(String[] args) {
        // ArrayList - Resizable array
        ArrayList<String> arrayList = new ArrayList<>();
        arrayList.add("Apple");
        arrayList.add("Banana");
        arrayList.add(1, "Orange");  // Insert at index 1
        System.out.println("ArrayList: " + arrayList);
        System.out.println("Element at index 0: " + arrayList.get(0));
        arrayList.remove("Banana");

        // LinkedList - Doubly linked list
        LinkedList<Integer> linkedList = new LinkedList<>();
        linkedList.add(10);
        linkedList.addFirst(5);   // Add at beginning
        linkedList.addLast(20);   // Add at end
        System.out.println("LinkedList: " + linkedList);

        // HashMap - Key-value pairs
        HashMap<String, Integer> hashMap = new HashMap<>();
        hashMap.put("John", 25);
        hashMap.put("Jane", 30);
        hashMap.put("Bob", 35);
        System.out.println("HashMap: " + hashMap);
        System.out.println("John's age: " + hashMap.get("John"));

        // Iterating collections
        for (String fruit : arrayList) {
            System.out.println("Fruit: " + fruit);
        }

        for (Map.Entry<String, Integer> entry : hashMap.entrySet()) {
            System.out.println(entry.getKey() + " = " + entry.getValue());
        }

        // Common operations
        System.out.println("ArrayList size: " + arrayList.size());
        System.out.println("HashMap contains 'John': " + hashMap.containsKey("John"));
        System.out.println("LinkedList is empty: " + linkedList.isEmpty());
    }
}

