public class WrapperClassExample {
    public static void main(String[] args) {
        // Primitive to Wrapper (Boxing)
        int primitive = 10;
        Integer wrapper1 = Integer.valueOf(primitive);  // Manual boxing
        Integer wrapper2 = primitive;  // Autoboxing (Java 5+)

        // Wrapper to Primitive (Unboxing)
        Integer wrapperNum = 20;
        int primitive2 = wrapperNum.intValue();  // Manual unboxing
        int primitive3 = wrapperNum;  // Auto-unboxing

        // All wrapper classes
        Byte byteWrapper = 1;
        Short shortWrapper = 100;
        Integer intWrapper = 1000;
        Long longWrapper = 10000L;
        Float floatWrapper = 3.14f;
        Double doubleWrapper = 3.14159;
        Character charWrapper = 'A';
        Boolean boolWrapper = true;

        // Utility methods
        String numberStr = "123";
        int parsed = Integer.parseInt(numberStr);  // String to int
        String converted = Integer.toString(456);   // int to String

        // Collections with wrapper classes
        List<Integer> numbers = new ArrayList<>();
        numbers.add(10);  // Autoboxing
        numbers.add(20);
        int first = numbers.get(0);  // Auto-unboxing

        // Comparing wrapper objects
        Integer a = 100;
        Integer b = 100;
        System.out.println(a == b);      // true (Integer cache -128 to 127)

        Integer c = 200;
        Integer d = 200;
        System.out.println(c == d);      // false (beyond cache range)
        System.out.println(c.equals(d)); // true (value comparison)

        // Null values (not possible with primitives)
        Integer nullValue = null;
        if (nullValue != null) {
            System.out.println("Value: " + nullValue);
        }
    }
}

public class ThisKeywordExample {
    private String name;
    private int age;

    // Constructor using 'this' to resolve naming conflicts
    public ThisKeywordExample(String name, int age) {
        this.name = name;  // 'this.name' refers to instance variable
        this.age = age;    // 'age' refers to parameter
    }

    // Constructor overloading with 'this()' call
    public ThisKeywordExample(String name) {
        this(name, 0);  // Calls the main constructor
    }

    public ThisKeywordExample() {
        this("Unknown");  // Calls single parameter constructor
    }

    // Setter methods using 'this'
    public ThisKeywordExample setName(String name) {
        this.name = name;
        return this;  // Return current object for method chaining
    }

    public ThisKeywordExample setAge(int age) {
        this.age = age;
        return this;  // Method chaining
    }

    // Method that passes 'this' as parameter
    public void printDetails() {
        displayObject(this);  // Pass current object
    }

    private void displayObject(ThisKeywordExample obj) {
        System.out.println("Name: " + obj.name + ", Age: " + obj.age);
    }

    // Without 'this' - ambiguity example
    public void setNameWrong(String name) {
        name = name;  // This doesn't change the instance variable!
        // Both 'name' refer to the parameter
    }

    public static void main(String[] args) {
        // Using constructors
        ThisKeywordExample person1 = new ThisKeywordExample("John", 25);
        ThisKeywordExample person2 = new ThisKeywordExample("Jane");
        ThisKeywordExample person3 = new ThisKeywordExample();

        // Method chaining using 'this'
