public class VariableTypesExample {
    // Static variable (class variable)
    static int staticCounter = 0;
    static final String COMPANY_NAME = "TechCorp";  // Static constant

    // Instance variables
    private String name;
    private int age;
    int id;  // Default access modifier

    public VariableTypesExample(String name, int age) {
        // Local variables
        String methodName = "Constructor";  // Local variable
        System.out.println("Inside " + methodName);

        // Accessing instance variables
        this.name = name;
        this.age = age;
        this.id = ++staticCounter;  // Increment static variable
    }

    public void displayInfo() {
        // Local variable
        String message = "Employee Information:";  // Exists only in this method

        System.out.println(message);
        System.out.println("ID: " + id);           // Instance variable
        System.out.println("Name: " + name);       // Instance variable
        System.out.println("Age: " + age);         // Instance variable
        System.out.println("Company: " + COMPANY_NAME); // Static variable

        // Local variable scope
        if (age > 18) {
            String status = "Adult";  // Local to if block
            System.out.println("Status: " + status);
        }
        // System.out.println(status);  // Error: out of scope
    }

    public static void printTotalEmployees() {
        // Static method can only access static variables directly
        System.out.println("Total employees: " + staticCounter);

        // Cannot access instance variables without object
        // System.out.println(name);  // Compilation error
    }

    public static void main(String[] args) {
        // Local variables in main method
        String mainMessage = "Creating employees...";
        System.out.println(mainMessage);

        // Creating objects
        VariableTypesExample emp1 = new VariableTypesExample("John", 25);
        VariableTypesExample emp2 = new VariableTypesExample("Jane", 30);

        emp1.displayInfo();
        emp2.displayInfo();

        // Accessing static variable
        System.out.println("Static counter: " + VariableTypesExample.staticCounter);
        printTotalEmployees();
    }
}

