// Processing annotations using reflection
public class AnnotationProcessor {

    public static void processTestCases(Class<?> testClass) {
        Method[] methods = testClass.getDeclaredMethods();

        for (Method method : methods) {
            if (method.isAnnotationPresent(TestCase.class)) {
                TestCase testCase = method.getAnnotation(TestCase.class);

                System.out.println("Test: " + testCase.value());
                System.out.println("Priority: " + testCase.priority());
                System.out.println("Tags: " + Arrays.toString(testCase.tags()));

                // Execute test method
                try {
                    method.invoke(testClass.newInstance());
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
    }
}

// Exception hierarchy
// Throwable
//   ├── Error (OutOfMemoryError, StackOverflowError)
//   └── Exception
//       ├── Checked Exceptions (IOException, SQLException)
//       └── RuntimeException (Unchecked)
//           ├── NullPointerException
//           ├── ArrayIndexOutOfBoundsException
//           └── IllegalArgumentException

// Try-catch-finally example
public class ExceptionHandling {

    public void readFile(String fileName) {
        FileReader file = null;
        BufferedReader reader = null;

        try {
            file = new FileReader(fileName);
            reader = new BufferedReader(file);
            String line = reader.readLine();
            System.out.println(line);

        } catch (FileNotFoundException e) {
            System.err.println("File not found: " + e.getMessage());
        } catch (IOException e) {
            System.err.println("IO error: " + e.getMessage());
        } finally {
            // Always executes - cleanup resources
            try {
                if (reader != null) reader.close();
                if (file != null) file.close();
            } catch (IOException e) {
                System.err.println("Error closing files: " + e.getMessage());
            }
        }
    }

    // Try-with-resources (Java 7+)
    public void readFileWithResources(String fileName) {
        try (FileReader file = new FileReader(fileName);
             BufferedReader reader = new BufferedReader(file)) {

            String line = reader.readLine();
            System.out.println(line);

        } catch (IOException e) {
            System.err.println("Error: " + e.getMessage());
        }
        // Resources automatically closed
    }

    // Custom exception
    public void validateAge(int age) throws InvalidAgeException {
        if (age < 0 || age > 150) {
            throw new InvalidAgeException("Invalid age: " + age);
        }
    }
}

// Custom checked exception
class InvalidAgeException extends Exception {
    public InvalidAgeException(String message) {
        super(message);
    }
}

