// Simple file upload
public class FileUploadTest {

    @Test
    public void testSimpleFileUpload() {
        WebDriver driver = new ChromeDriver();
        driver.get("https://example.com/upload");

        // Direct file upload using sendKeys
        WebElement fileInput = driver.findElement(By.xpath("//input[@type='file']"));
        String filePath = System.getProperty("user.dir") + "/testfiles/document.pdf";
        fileInput.sendKeys(filePath);

        // Submit the form
        driver.findElement(By.id("submitBtn")).click();

        // Verify upload success
        WebElement successMsg = driver.findElement(By.className("success"));
        Assert.assertTrue(successMsg.getText().contains("Upload successful"));

        driver.quit();
    }

    // File download configuration
    @Test
    public void testFileDownload() {
        // Chrome download preferences
        HashMap<String, Object> chromePrefs = new HashMap<>();
        chromePrefs.put("profile.default_content_settings.popups", 0);
        chromePrefs.put("download.default_directory", System.getProperty("user.dir") + "/downloads");
        chromePrefs.put("download.prompt_for_download", false);
        chromePrefs.put("plugins.always_open_pdf_externally", true);

        ChromeOptions options = new ChromeOptions();
        options.setExperimentalOption("prefs", chromePrefs);

        WebDriver driver = new ChromeDriver(options);
        driver.get("https://example.com/download");

        // Click download link
        driver.findElement(By.linkText("Download PDF")).click();

        // Wait for download to complete
        String downloadPath = System.getProperty("user.dir") + "/downloads/document.pdf";
        File downloadedFile = new File(downloadPath);

        // Wait for file to exist
        WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(30));
        wait.until(ExpectedConditions.refreshed(
            ExpectedConditions.presenceOfElementLocated(By.tagName("body"))
        ));

        // Verify file exists
        int attempts = 0;
        while (!downloadedFile.exists() && attempts < 10) {
            Thread.sleep(1000);
            attempts++;
        }

        Assert.assertTrue("File was not downloaded", downloadedFile.exists());
        Assert.assertTrue("Downloaded file is empty", downloadedFile.length() > 0);

        driver.quit();
    }

    // Multiple file upload
    @Test
    public void testMultipleFileUpload() {
        WebDriver driver = new ChromeDriver();
        driver.get("https://example.com/multi-upload");

        WebElement fileInput = driver.findElement(By.xpath("//input[@type='file'][@multiple]"));

        // Upload multiple files
        String file1 = System.getProperty("user.dir") + "/testfiles/doc1.pdf";
        String file2 = System.getProperty("user.dir") + "/testfiles/doc2.jpg";
        String file3 = System.getProperty("user.dir") + "/testfiles/doc3.txt";

        String allFiles = file1 + "\n" + file2 + "\n" + file3;
        fileInput.sendKeys(allFiles);

        driver.findElement(By.id("uploadBtn")).click();

        // Verify all files uploaded
        List<WebElement> uploadedFiles = driver.findElements(By.className("uploaded-file"));
        Assert.assertEquals("Expected 3 uploaded files", 3, uploadedFiles.size());

        driver.quit();
    }
}

