        alert.accept(); // Click OK

        driver.quit();
    }

    @Test
    public void testConfirmationAlert() {
        WebDriver driver = new ChromeDriver();
        driver.get("https://example.com/alerts");

        // Trigger confirmation dialog
        driver.findElement(By.id("confirmAlert")).click();

        Alert alert = driver.switchTo().alert();
        String alertText = alert.getText();
        Assert.assertEquals("Are you sure?", alertText);

        // Choose Cancel
        alert.dismiss();

        // Verify result
        WebElement result = driver.findElement(By.id("result"));
        Assert.assertEquals("Cancelled", result.getText());

        driver.quit();
    }

    @Test
    public void testPromptAlert() {
        WebDriver driver = new ChromeDriver();
        driver.get("https://example.com/alerts");

        // Trigger prompt dialog
        driver.findElement(By.id("promptAlert")).click();

        Alert alert = driver.switchTo().alert();
        String alertText = alert.getText();
        System.out.println("Prompt text: " + alertText);

        // Enter text in prompt
        alert.sendKeys("Test Input");
        alert.accept();

        // Verify entered text appears on page
        WebElement result = driver.findElement(By.id("promptResult"));
        Assert.assertEquals("Test Input", result.getText());

        driver.quit();
    }

    // Handling modal dialogs (not JavaScript alerts)
    @Test
    public void testModalDialog() {
        WebDriver driver = new ChromeDriver();
        driver.get("https://example.com/modal");

        // Open modal
        driver.findElement(By.id("openModal")).click();

        // Wait for modal to appear
        WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(10));
        WebElement modal = wait.until(ExpectedConditions.visibilityOfElementLocated(By.id("myModal")));

        // Interact with modal elements
        driver.findElement(By.id("modalInput")).sendKeys("Modal input text");
        driver.findElement(By.id("modalSubmit")).click();

        // Wait for modal to close
        wait.until(ExpectedConditions.invisibilityOfElementLocated(By.id("myModal")));

        // Verify result
        WebElement result = driver.findElement(By.id("modalResult"));
        Assert.assertEquals("Modal input text", result.getText());

        driver.quit();
    }

    // Alert utility methods
    public class AlertUtils {

        public static boolean isAlertPresent(WebDriver driver) {
            try {
                driver.switchTo().alert();
                return true;
            } catch (NoAlertPresentException e) {
                return false;
            }
        }

        public static String handleAlertAndGetText(WebDriver driver, boolean accept) {
            String alertText = "";

            if (isAlertPresent(driver)) {
                Alert alert = driver.switchTo().alert();
                alertText = alert.getText();

                if (accept) {
                    alert.accept();
                } else {
                    alert.dismiss();
                }
            }

            return alertText;
        }

        public static void waitForAlert(WebDriver driver, int timeoutSeconds) {
            WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(timeoutSeconds));
            wait.until(ExpectedConditions.alertIsPresent());
        }
    }
}

