        driver.get("https://example.com/frames");

        // Find frame element first
        WebElement frameElement = driver.findElement(By.xpath("//iframe[@class='content-frame']"));

        // Switch to frame using WebElement
        driver.switchTo().frame(frameElement);

        // Interact with frame content
        List<WebElement> frameLinks = driver.findElements(By.tagName("a"));
        System.out.println("Number of links in frame: " + frameLinks.size());

        if (!frameLinks.isEmpty()) {
            frameLinks.get(0).click();
        }

        // Switch back to main content
        driver.switchTo().defaultContent();

        driver.quit();
    }

    // Handling nested frames
    @Test
    public void testNestedFrames() {
        WebDriver driver = new ChromeDriver();
        driver.get("https://example.com/nested-frames");

        // Switch to parent frame first
        driver.switchTo().frame("parentFrame");

        // Then switch to child frame
        driver.switchTo().frame("childFrame");

        // Interact with deeply nested content
        driver.findElement(By.id("nestedElement")).sendKeys("Nested frame text");

        // Switch to parent frame (one level up)
        driver.switchTo().parentFrame();

        // Interact with parent frame content
        driver.findElement(By.id("parentElement")).click();

        // Switch back to main content (top level)
        driver.switchTo().defaultContent();

        driver.quit();
    }

    // Dynamic frame handling
    @Test
    public void testDynamicFrames() {
        WebDriver driver = new ChromeDriver();
        WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(10));
        driver.get("https://example.com/dynamic-frames");

        // Wait for frame to load
        wait.until(ExpectedConditions.frameToBeAvailableAndSwitchToIt(By.id("dynamicFrame")));

        // Now we're automatically switched to the frame
        WebElement frameContent = wait.until(
            ExpectedConditions.presenceOfElementLocated(By.id("frameContent"))
        );

        Assert.assertTrue(frameContent.isDisplayed());
        frameContent.sendKeys("Dynamic frame interaction");

        // Switch back to default content
        driver.switchTo().defaultContent();

        driver.quit();
    }

    // Frame utility methods
    public class FrameUtils {

        public static boolean isFramePresent(WebDriver driver, String frameLocator) {
            try {
                driver.switchTo().frame(frameLocator);
                driver.switchTo().defaultContent();
                return true;
            } catch (NoSuchFrameException e) {
                return false;
            }
        }

        public static void switchToFrameAndPerformAction(WebDriver driver, String frameId, Runnable action) {
            driver.switchTo().frame(frameId);
            action.run();
            driver.switchTo().defaultContent();
        }

        public static int getFrameCount(WebDriver driver) {
            List<WebElement> frames = driver.findElements(By.tagName("iframe"));
            frames.addAll(driver.findElements(By.tagName("frame")));
            return frames.size();
        }
    }
}

// Drag and Drop examples
public class DragDropTest {

    @Test
    public void testSimpleDragAndDrop() {
        WebDriver driver = new ChromeDriver();
        Actions actions = new Actions(driver);
        driver.get("https://example.com/drag-drop");

        // Find source and target elements
        WebElement source = driver.findElement(By.id("draggable"));
        WebElement target = driver.findElement(By.id("droppable"));

        // Perform drag and drop
        actions.dragAndDrop(source, target).perform();

        // Verify drop was successful
        WebElement result = driver.findElement(By.id("dropResult"));
        Assert.assertEquals("Dropped successfully!", result.getText());

        driver.quit();
    }

    @Test
    public void testDragAndDropWithOffset() {
        WebDriver driver = new ChromeDriver();
        Actions actions = new Actions(driver);
        driver.get("https://example.com/drag-drop-canvas");

        WebElement draggableElement = driver.findElement(By.id("moveable"));

        // Drag element by offset (x, y pixels)
        actions.dragAndDropBy(draggableElement, 100, 50).perform();

        // Verify new position
        String newPosition = draggableElement.getCssValue("transform");
        Assert.assertTrue(newPosition.contains("100"));

        driver.quit();
    }

    @Test
    public void testComplexDragAndDrop() {
        WebDriver driver = new ChromeDriver();
        Actions actions = new Actions(driver);
        driver.get("https://example.com/sortable-list");

        WebElement item1 = driver.findElement(By.xpath("//li[text()='Item 1']"));
        WebElement item3 = driver.findElement(By.xpath("//li[text()='Item 3']"));

        // Build complex action chain
        actions.clickAndHold(item1)
               .moveToElement(item3)
               .moveByOffset(0, 30)
               .release()
               .build()
               .perform();

        // Verify new order
        List<WebElement> listItems = driver.findElements(By.xpath("//ul[@id='sortable']/li"));
        Assert.assertEquals("Item 2", listItems.get(0).getText());
        Assert.assertEquals("Item 3", listItems.get(1).getText());
        Assert.assertEquals("Item 1", listItems.get(2).getText());

        driver.quit();
    }

    @Test
    public void testDragAndDropMultipleElements() {
        WebDriver driver = new ChromeDriver();
        Actions actions = new Actions(driver);
        driver.get("https://example.com/multiple-drag-drop");

        // Multiple drag operations
        WebElement source1 = driver.findElement(By.id("source1"));
        WebElement source2 = driver.findElement(By.id("source2"));
        WebElement target1 = driver.findElement(By.id("target1"));
        WebElement target2 = driver.findElement(By.id("target2"));

        // Perform multiple drags in sequence
        actions.dragAndDrop(source1, target1)
               .pause(Duration.ofMillis(500))
               .dragAndDrop(source2, target2)
               .perform();

        // Verify both drops
        Assert.assertTrue(target1.findElements(By.className("dropped-item")).size() > 0);
        Assert.assertTrue(target2.findElements(By.className("dropped-item")).size() > 0);

        driver.quit();
    }

    // HTML5 Drag and Drop (requires JavaScript execution)
    @Test
    public void testHTML5DragDrop() {
        WebDriver driver = new ChromeDriver();
        JavascriptExecutor js = (JavascriptExecutor) driver;
        driver.get("https://example.com/html5-drag-drop");

        WebElement source = driver.findElement(By.id("html5-source"));
        WebElement target = driver.findElement(By.id("html5-target"));

        // JavaScript for HTML5 drag and drop
        String dragDropScript = 
            "function createEvent(typeOfEvent) {" +
            "    var event = document.createEvent(\"CustomEvent\");" +
            "    event.initCustomEvent(typeOfEvent, true, true, null);" +
            "    event.dataTransfer = {" +
            "        data: {}," +
            "        setData: function(key, val) { this.data[key] = val; }," +
            "        getData: function(key) { return this.data[key]; }" +
            "    };" +
            "    return event;" +
            "}" +
            "function dispatchEvent(element, event, transferData) {" +
            "    if (transferData !== undefined) {" +
            "        event.dataTransfer = transferData;" +
            "    }" +
            "    if (element.dispatchEvent) {" +
            "        element.dispatchEvent(event);" +
            "    }" +
            "}" +
            "var source = arguments[0];" +
            "var target = arguments[1];" +
            "var dragStartEvent = createEvent('dragstart');" +
            "dispatchEvent(source, dragStartEvent);" +
            "var dropEvent = createEvent('drop');" +
            "dispatchEvent(target, dropEvent, dragStartEvent.dataTransfer);" +
            "var dragEndEvent = createEvent('dragend');" +
            "dispatchEvent(source, dragEndEvent, dropEvent.dataTransfer);";

        js.executeScript(dragDropScript, source, target);

        // Verify HTML5 drag drop result
        WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(5));
        WebElement result = wait.until(ExpectedConditions.presenceOfElementLocated(By.id("drop-result")));
        Assert.assertEquals("HTML5 Drop successful!", result.getText());

        driver.quit();
    }

    // Utility methods for drag and drop
    public class DragDropUtils {

        public static void dragAndDropWithRetry(WebDriver driver, WebElement source, WebElement target, int maxRetries) {
            Actions actions = new Actions(driver);

            for (int i = 0; i < maxRetries; i++) {
                try {
                    actions.dragAndDrop(source, target).perform();
                    // Add verification logic here
                    break;
                } catch (Exception e) {
                    if (i == maxRetries - 1) {
                        throw e;
                    }
                    // Wait before retry
                    try {
                        Thread.sleep(1000);
                    } catch (InterruptedException ie) {
                        Thread.currentThread().interrupt();
                    }
                }
            }
        }

        public static void smoothDragAndDrop(WebDriver driver, WebElement source, WebElement target) {
            Actions actions = new Actions(driver);

            actions.clickAndHold(source)
                   .pause(Duration.ofMillis(300))
                   .moveToElement(target)
                   .pause(Duration.ofMillis(300))
                   .release()
                   .perform();
        }
    }
}

