// REST Assured Performance Test
import static io.restassured.RestAssured.*;
import org.testng.annotations.Test;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class APIPerformanceTest {

    @Test
    public void loadTestAPI() throws InterruptedException {
        int numberOfThreads = 50;
        ExecutorService executor = Executors.newFixedThreadPool(numberOfThreads);

        long startTime = System.currentTimeMillis();

        for (int i = 0; i < 1000; i++) {
            executor.submit(() -> {
                long requestStart = System.currentTimeMillis();

                given()
                    .when()
                    .get("/api/users")
                    .then()
                    .statusCode(200)
                    .time(lessThan(2000L)); // Response time < 2 seconds

                long responseTime = System.currentTimeMillis() - requestStart;
                System.out.println("Response time: " + responseTime + "ms");
            });
        }

        executor.shutdown();
        executor.awaitTermination(5, TimeUnit.MINUTES);

        long totalTime = System.currentTimeMillis() - startTime;
        System.out.println("Total test time: " + totalTime + "ms");
        System.out.println("Throughput: " + (1000.0 / (totalTime / 1000.0)) + " requests/sec");
    }

    @Test
    public void spikeTest() {
        // Sudden increase in load
        for (int i = 0; i < 500; i++) {
            given()
                .when()
                .get("/api/products")
                .then()
                .statusCode(anyOf(is(200), is(503))) // Allow 503 under spike
                .time(lessThan(5000L));
        }
    }
}

