        given()
            .when()
            .get("/api/orders/user/123")
            .then()
            .statusCode(500);
    }

    // Circuit should be open now - fast failure
    long startTime = System.currentTimeMillis();
    given()
        .when()
        .get("/api/orders/user/123")
        .then()
        .statusCode(503); // Circuit open

    long responseTime = System.currentTimeMillis() - startTime;
    assertTrue("Circuit breaker should fail fast", responseTime < 1000);
}

