// 4. SLA Testing
@Test
public void testSLA() {
    List<Long> responseTimes = new ArrayList<>();
    int successCount = 0;
    int totalRequests = 100;

    for (int i = 0; i < totalRequests; i++) {
        long startTime = System.currentTimeMillis();

        Response response = given()
            .when()
            .get("/api/products");

        long responseTime = System.currentTimeMillis() - startTime;
        responseTimes.add(responseTime);

        if (response.getStatusCode() == 200) {
            successCount++;
        }
    }

    // SLA Validation
    double successRate = (double) successCount / totalRequests * 100;
    assertTrue("SLA: Success rate should be >= 99%", successRate >= 99.0);

    double avgResponseTime = responseTimes.stream().mapToLong(Long::longValue).average().orElse(0);
    assertTrue("SLA: Average response time should be < 200ms", avgResponseTime < 200);

    // 95th percentile
    Collections.sort(responseTimes);
    long p95 = responseTimes.get((int) (totalRequests * 0.95));
    assertTrue("SLA: 95th percentile should be < 500ms", p95 < 500);
}

