// Custom Test Listener
public class CustomTestListener implements ITestListener {

    @Override
    public void onTestStart(ITestResult result) {
        System.out.println("Test Started: " + result.getMethod().getMethodName());
        // Log test start time, setup test data
    }

    @Override
    public void onTestSuccess(ITestResult result) {
        System.out.println("Test Passed: " + result.getMethod().getMethodName());
        // Log success, cleanup
    }

    @Override
    public void onTestFailure(ITestResult result) {
        System.out.println("Test Failed: " + result.getMethod().getMethodName());

        // Capture screenshot
        String screenshotPath = captureScreenshot(result.getMethod().getMethodName());

        // Attach to test result
        System.setProperty("org.uncommons.reportng.escape-output", "false");
        System.out.println("Screenshot: <a href='" + screenshotPath + "'>View</a>");

        // Log failure details
        System.out.println("Failure Reason: " + result.getThrowable().getMessage());
    }

    private String captureScreenshot(String testName) {
        try {
            TakesScreenshot screenshot = (TakesScreenshot) driver;
            File source = screenshot.getScreenshotAs(OutputType.FILE);
            String destination = "screenshots/" + testName + "_" + System.currentTimeMillis() + ".png";
            FileUtils.copyFile(source, new File(destination));
            return destination;
        } catch (Exception e) {
            e.printStackTrace();
            return "Screenshot failed";
        }
    }
}

