// Soft Assertions Example
public class SoftAssertionTest {

    @Test
    public void testMultipleConditions() {
        SoftAssert softAssert = new SoftAssert();

        // These assertions won't stop execution if they fail
        softAssert.assertEquals("Apple", "Orange", "Fruit name mismatch");
        softAssert.assertTrue(false, "Boolean condition failed");
        softAssert.assertNotNull(null, "Object should not be null");

        System.out.println("This line will execute even if assertions above failed");

        // This will throw an exception if any soft assertion failed
        softAssert.assertAll();
    }

    @Test
    public void testWebPageValidation() {
        SoftAssert softAssert = new SoftAssert();

        // Validate multiple elements without stopping
        softAssert.assertTrue(driver.findElement(By.id("header")).isDisplayed(), 
                            "Header not displayed");
        softAssert.assertEquals(driver.getTitle(), "Expected Title", 
                              "Title mismatch");
        softAssert.assertTrue(driver.findElement(By.id("footer")).isDisplayed(), 
                            "Footer not displayed");

        // Collect all validation results
        softAssert.assertAll();
    }
}

