// Excel Data Provider
public class ExcelDataProvider {

    public static Object[][] getTestData(String fileName, String sheetName) {
        try {
            FileInputStream file = new FileInputStream("testdata/" + fileName);
            XSSFWorkbook workbook = new XSSFWorkbook(file);
            XSSFSheet sheet = workbook.getSheet(sheetName);

            int rowCount = sheet.getLastRowNum();
            int colCount = sheet.getRow(0).getLastCellNum();

            Object[][] data = new Object[rowCount][colCount];

            for (int i = 1; i <= rowCount; i++) {
                for (int j = 0; j < colCount; j++) {
                    data[i-1][j] = sheet.getRow(i).getCell(j).toString();
                }
            }

            workbook.close();
            file.close();
            return data;

        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }
}

// JSON Data Provider
public class JSONDataProvider {

    public static Object[][] getJSONData(String fileName) {
        try {
            String jsonData = new String(Files.readAllBytes(
                Paths.get("testdata/" + fileName)));

            JSONArray jsonArray = new JSONArray(jsonData);
            Object[][] data = new Object[jsonArray.length()][];

            for (int i = 0; i < jsonArray.length(); i++) {
                JSONObject jsonObject = jsonArray.getJSONObject(i);
                data[i] = new Object[] {
                    jsonObject.getString("username"),
                    jsonObject.getString("password"),
                    jsonObject.getBoolean("shouldSucceed"),
                    jsonObject.getString("expectedMessage")
                };
            }

            return data;

        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }
}

