// ExtentReports Implementation
public class ExtentManager {
    private static ExtentReports extent;
    private static ExtentTest test;
    private static String reportPath = "reports/ExtentReport.html";

    public static void initReport() {
        ExtentHtmlReporter htmlReporter = new ExtentHtmlReporter(reportPath);
        htmlReporter.config().setDocumentTitle("Automation Test Report");
        htmlReporter.config().setReportName("Test Execution Report");
        htmlReporter.config().setTheme(Theme.STANDARD);

        extent = new ExtentReports();
        extent.attachReporter(htmlReporter);
        extent.setSystemInfo("OS", System.getProperty("os.name"));
        extent.setSystemInfo("Browser", "Chrome");
        extent.setSystemInfo("Environment", "QA");
    }

    public static void createTest(String testName, String description) {
        test = extent.createTest(testName, description);
    }

    public static void logInfo(String message) {
        test.info(message);
    }

    public static void logPass(String message) {
        test.pass(message);
    }

    public static void logFail(String message) {
        test.fail(message);
    }

    public static void attachScreenshot(String screenshotPath) {
        test.addScreenCaptureFromPath(screenshotPath);
    }

    public static void flushReport() {
        extent.flush();
    }
}

// ExtentReports TestNG Listener
public class ExtentReportListener implements ITestListener {

    @Override
    public void onStart(ISuite suite) {
        ExtentManager.initReport();
    }

    @Override
    public void onTestStart(ITestResult result) {
        String testName = result.getMethod().getMethodName();
        String description = result.getMethod().getDescription();
        ExtentManager.createTest(testName, description);
        ExtentManager.logInfo("Test Started: " + testName);
    }

    @Override
    public void onTestSuccess(ITestResult result) {
        ExtentManager.logPass("Test Passed: " + result.getMethod().getMethodName());
    }

    @Override
    public void onTestFailure(ITestResult result) {
        String testName = result.getMethod().getMethodName();
        ExtentManager.logFail("Test Failed: " + testName);
        ExtentManager.logFail("Failure Reason: " + result.getThrowable().getMessage());

        // Capture screenshot
        String screenshotPath = captureScreenshot(testName);
        ExtentManager.attachScreenshot(screenshotPath);
    }

    @Override
    public void onFinish(ISuite suite) {
        ExtentManager.flushReport();
    }

    private String captureScreenshot(String testName) {
        try {
            TakesScreenshot screenshot = (TakesScreenshot) BaseTest.getDriver();
            File source = screenshot.getScreenshotAs(OutputType.FILE);
            String destination = "screenshots/" + testName + "_" + 
                               System.currentTimeMillis() + ".png";
            FileUtils.copyFile(source, new File(destination));
            return destination;
        } catch (Exception e) {
            e.printStackTrace();
            return "Screenshot capture failed";
