// Performance Testing Framework
public class DatabasePerformanceTest {

    @Test
    public void testQueryPerformance() throws SQLException {
        String slowQuery = "SELECT * FROM orders o JOIN customers c ON o.customer_id = c.id WHERE o.order_date BETWEEN ? AND ?";

        long startTime = System.currentTimeMillis();

        PreparedStatement stmt = connection.prepareStatement(slowQuery);
        stmt.setDate(1, Date.valueOf("2023-01-01"));
        stmt.setDate(2, Date.valueOf("2023-12-31"));

        ResultSet rs = stmt.executeQuery();

        // Process results
        int recordCount = 0;
        while (rs.next()) {
            recordCount++;
        }

        long endTime = System.currentTimeMillis();
        long executionTime = endTime - startTime;

        System.out.println("Query executed in: " + executionTime + "ms");
        System.out.println("Records returned: " + recordCount);

        // Assert performance threshold
        assertTrue("Query took too long: " + executionTime + "ms", executionTime < 5000);
    }

    // Test with large dataset
    @Test
    public void testLargeDatasetPerformance() throws SQLException {
        // Insert test data
        String insertSQL = "INSERT INTO test_table (data) VALUES (?)";
        PreparedStatement stmt = connection.prepareStatement(insertSQL);

        long startTime = System.currentTimeMillis();

        // Batch insert for better performance
        for (int i = 0; i < 100000; i++) {
            stmt.setString(1, "Test data " + i);
            stmt.addBatch();

            if (i % 1000 == 0) {
                stmt.executeBatch();
            }
        }
        stmt.executeBatch();

        long endTime = System.currentTimeMillis();
        long insertTime = endTime - startTime;

        System.out.println("Batch insert completed in: " + insertTime + "ms");
        assertTrue("Batch insert took too long", insertTime < 30000);
    }

    // Test concurrent access
    @Test
    public void testConcurrentAccess() throws InterruptedException {
        int threadCount = 10;
        CountDownLatch latch = new CountDownLatch(threadCount);
        List<Long> executionTimes = Collections.synchronizedList(new ArrayList<>());

        for (int i = 0; i < threadCount; i++) {
            new Thread(() -> {
                try {
                    Connection conn = DriverManager.getConnection(DB_URL, USERNAME, PASSWORD);

                    long startTime = System.currentTimeMillis();

                    String sql = "SELECT COUNT(*) FROM users WHERE age > 25";
                    ResultSet rs = conn.createStatement().executeQuery(sql);
                    rs.next();
                    int count = rs.getInt(1);

                    long endTime = System.currentTimeMillis();
                    executionTimes.add(endTime - startTime);

                    conn.close();
                } catch (SQLException e) {
                    e.printStackTrace();
                } finally {
                    latch.countDown();
                }
            }).start();
        }

        latch.await(30, TimeUnit.SECONDS);

        // Calculate average execution time
        double avgTime = executionTimes.stream().mapToLong(Long::longValue).average().orElse(0);
        System.out.println("Average concurrent query time: " + avgTime + "ms");

        assertTrue("Concurrent queries too slow", avgTime < 1000);
    }
}

