// ACID Properties Testing
public class ACIDPropertiesTest {

    @Test
    public void testAtomicity() throws SQLException {
        // Test that all operations in transaction succeed or all rollback
        connection.setAutoCommit(false);

        try {
            // Start transaction
            PreparedStatement debitStmt = connection.prepareStatement(
                "UPDATE accounts SET balance = balance - ? WHERE id = ?");
            PreparedStatement creditStmt = connection.prepareStatement(
                "UPDATE accounts SET balance = balance + ? WHERE id = ?");

            // Transfer $100 from account 1 to account 2
            debitStmt.setBigDecimal(1, new BigDecimal("100.00"));
            debitStmt.setInt(2, 1);
            int debitRows = debitStmt.executeUpdate();

            creditStmt.setBigDecimal(1, new BigDecimal("100.00"));
            creditStmt.setInt(2, 999999); // Non-existent account
            int creditRows = creditStmt.executeUpdate();

            if (debitRows == 0 || creditRows == 0) {
                connection.rollback();
                fail("Transaction should rollback when part fails");
            }

            connection.commit();

        } catch (SQLException e) {
            // Expected failure - verify rollback
            connection.rollback();

            // Verify original balances unchanged
            PreparedStatement checkStmt = connection.prepareStatement(
                "SELECT balance FROM accounts WHERE id = 1");
            ResultSet rs = checkStmt.executeQuery();
            rs.next();
            BigDecimal balance = rs.getBigDecimal("balance");

            // Should be original balance (atomicity preserved)
            assertEquals("Balance should be unchanged after rollback", 
                new BigDecimal("1000.00"), balance);
        } finally {
            connection.setAutoCommit(true);
        }
    }

    @Test
    public void testConsistency() throws SQLException {
        // Test that constraints are maintained
        connection.setAutoCommit(false);

        try {
            // Try to insert order without valid customer (violates FK constraint)
            PreparedStatement stmt = connection.prepareStatement(
                "INSERT INTO orders (customer_id, product_id, quantity) VALUES (?, ?, ?)");
            stmt.setInt(1, 99999); // Non-existent customer
            stmt.setInt(2, 1);
            stmt.setInt(3, 1);

            stmt.executeUpdate();
            connection.commit();

            fail("Should not allow order with invalid customer_id");

        } catch (SQLException e) {
            // Expected - foreign key constraint violation
            connection.rollback();
            assertTrue("Should be constraint violation", 
                e.getMessage().toLowerCase().contains("constraint"));
        } finally {
            connection.setAutoCommit(true);
        }
    }

    @Test
    public void testIsolation() throws InterruptedException {
        // Test that concurrent transactions don't interfere
        CountDownLatch latch = new CountDownLatch(2);
        List<BigDecimal> results = Collections.synchronizedList(new ArrayList<>());

        // Transaction 1: Read balance
        Thread t1 = new Thread(() -> {
            try (Connection conn1 = getConnection()) {
                conn1.setAutoCommit(false);
                conn1.setTransactionIsolation(Connection.TRANSACTION_READ_COMMITTED);

                PreparedStatement stmt = conn1.prepareStatement(
                    "SELECT balance FROM accounts WHERE id = 1");
                ResultSet rs = stmt.executeQuery();
                rs.next();
                results.add(rs.getBigDecimal("balance"));

                Thread.sleep(2000); // Hold transaction
                conn1.commit();

            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                latch.countDown();
            }
        });

        // Transaction 2: Update balance
        Thread t2 = new Thread(() -> {
            try (Connection conn2 = getConnection()) {
                Thread.sleep(500); // Start after t1

                conn2.setAutoCommit(false);
                PreparedStatement stmt = conn2.prepareStatement(
                    "UPDATE accounts SET balance = balance + ? WHERE id = 1");
                stmt.setBigDecimal(1, new BigDecimal("50.00"));
                stmt.executeUpdate();
                conn2.commit();

            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                latch.countDown();
            }
        });

        t1.start();
        t2.start();

        latch.await(10, TimeUnit.SECONDS);

        // Verify isolation - t1 should see consistent data
        assertEquals("Should see original balance due to isolation", 
            new BigDecimal("1000.00"), results.get(0));
    }

    @Test
    public void testDurability() throws SQLException {
        // Test that committed data persists
        BigDecimal originalBalance;

        // Get original balance
        PreparedStatement selectStmt = connection.prepareStatement(
            "SELECT balance FROM accounts WHERE id = 1");
        ResultSet rs = selectStmt.executeQuery();
        rs.next();
        originalBalance = rs.getBigDecimal("balance");

        // Perform transaction and commit
        connection.setAutoCommit(false);
        PreparedStatement updateStmt = connection.prepareStatement(
            "UPDATE accounts SET balance = balance + ? WHERE id = 1");
        updateStmt.setBigDecimal(1, new BigDecimal("25.00"));
        updateStmt.executeUpdate();
        connection.commit();
        connection.setAutoCommit(true);

        // Simulate system restart by closing and reopening connection
        connection.close();
        connection = DriverManager.getConnection(DB_URL, USERNAME, PASSWORD);

        // Verify data persisted
        PreparedStatement checkStmt = connection.prepareStatement(
            "SELECT balance FROM accounts WHERE id = 1");
        ResultSet newRs = checkStmt.executeQuery();
        newRs.next();
        BigDecimal newBalance = newRs.getBigDecimal("balance");

        assertEquals("Balance should persist after commit", 
            originalBalance.add(new BigDecimal("25.00")), newBalance);
    }
}

// MongoDB Testing Examples
public class NoSQLDatabaseTest {
    private MongoClient mongoClient;
    private MongoDatabase database;
    private MongoCollection<Document> collection;

    @BeforeClass
    public void setUp() {
        mongoClient = MongoClients.create("mongodb://localhost:27017");
        database = mongoClient.getDatabase("testdb");
        collection = database.getCollection("users");
    }

    @Test
    public void testDocumentInsertion() {
        // Test inserting document with flexible schema
        Document user = new Document("name", "John Doe")
            .append("email", "john@example.com")
            .append("age", 30)
            .append("tags", Arrays.asList("developer", "java", "mongodb"))
            .append("address", new Document("street", "123 Main St")
                .append("city", "New York")
                .append("zipcode", "10001"));

        collection.insertOne(user);

        // Verify insertion
        Document found = collection.find(eq("email", "john@example.com")).first();
        assertNotNull("User should be found", found);
        assertEquals("John Doe", found.getString("name"));
        assertEquals(30, found.getInteger("age").intValue());

        // Test nested document
        Document address = found.get("address", Document.class);
        assertEquals("New York", address.getString("city"));
    }

    @Test
    public void testSchemaFlexibility() {
        // Insert documents with different schemas
        Document user1 = new Document("name", "Alice")
            .append("email", "alice@example.com")
            .append("age", 25);

        Document user2 = new Document("name", "Bob")
            .append("email", "bob@example.com")
            .append("department", "Engineering")
            .append("skills", Arrays.asList("Python", "Docker"))
            .append("remote", true);

        collection.insertMany(Arrays.asList(user1, user2));

        // Query with flexible fields
        long countWithAge = collection.countDocuments(exists("age"));
        long countWithDepartment = collection.countDocuments(exists("department"));

        assertTrue("Some users should have age field", countWithAge > 0);
        assertTrue("Some users should have department field", countWithDepartment > 0);
    }

    @Test
    public void testArrayAndNestedQueries() {
        // Test querying arrays and nested documents
        Document developer = new Document("name", "Carol")
            .append("email", "carol@example.com")
            .append("skills", Arrays.asList("Java", "Spring", "MongoDB"))
            .append("experience", new Document("years", 5)
                .append("level", "senior")
                .append("projects", Arrays.asList("E-commerce", "Banking")));

        collection.insertOne(developer);

        // Query by array element
        Document foundBySkill = collection.find(in("skills", "Java")).first();
        assertNotNull("Should find user with Java skill", foundBySkill);

        // Query nested field
        Document foundByExperience = collection.find(
            eq("experience.level", "senior")
        ).first();
        assertNotNull("Should find senior developer", foundByExperience);

        // Query nested array
        Document foundByProject = collection.find(
            in("experience.projects", "Banking")
        ).first();
        assertNotNull("Should find developer with Banking project", foundByProject);
    }

    @Test
    public void testAggregationPipeline() {
        // Insert test data
        collection.insertMany(Arrays.asList(
            new Document("name", "Dev1").append("department", "Engineering").append("salary", 80000),
            new Document("name", "Dev2").append("department", "Engineering").append("salary", 90000),
            new Document("name", "QA1").append("department", "QA").append("salary", 70000),
            new Document("name", "QA2").append("department", "QA").append("salary", 75000)
        ));

        // Test aggregation pipeline
        List<Document> pipeline = Arrays.asList(
            new Document("$group", new Document("_id", "$department")
                .append("avgSalary", new Document("$avg", "$salary"))
                .append("count", new Document("$sum", 1))),
            new Document("$sort", new Document("avgSalary", -1))
        );

        List<Document> results = collection.aggregate(pipeline).into(new ArrayList<>());

        assertEquals("Should have 2 departments", 2, results.size());
        assertEquals("Engineering should have higher avg salary", 
            "Engineering", results.get(0).getString("_id"));
    }

    @Test
    public void testEventualConsistency() throws InterruptedException {
        // Test eventual consistency in replica set
        Document testDoc = new Document("testId", "consistency-test")
            .append("value", "initial");

        // Write to primary
        collection.insertOne(testDoc);

        // Simulate slight delay for replication
        Thread.sleep(100);

        // Read from secondary (may not be immediately consistent)
        ReadPreference secondaryPreferred = ReadPreference.secondaryPreferred();
        MongoCollection<Document> secondaryCollection = 
            collection.withReadPreference(secondaryPreferred);

        Document found = secondaryCollection.find(
            eq("testId", "consistency-test")
        ).first();

        // In production, this might be null due to eventual consistency
        // For testing, we verify the behavior
        if (found != null) {
            assertEquals("initial", found.getString("value"));
        }
    }

    @AfterClass
    public void tearDown() {
        if (collection != null) {
            collection.drop();
        }
        if (mongoClient != null) {
            mongoClient.close();
        }
    }
}

