// Database Migration Testing Framework
public class DatabaseMigrationTest {
    private Connection connection;
    private MigrationRunner migrationRunner;

    @BeforeClass
    public void setUp() throws SQLException {
        connection = DriverManager.getConnection(TEST_DB_URL, USERNAME, PASSWORD);
        migrationRunner = new MigrationRunner(connection);
    }

    @Test
    public void testSchemaVersioning() throws SQLException {
        // Verify initial schema version
        int currentVersion = migrationRunner.getCurrentSchemaVersion();
        assertEquals("Initial schema version should be 0", 0, currentVersion);

        // Apply first migration
        Migration migration1 = new Migration(1, 
            "CREATE TABLE users (id INT PRIMARY KEY, name VARCHAR(100))",
            "DROP TABLE users");

        migrationRunner.applyMigration(migration1);

        // Verify version updated
        assertEquals("Schema version should be updated", 1, 
            migrationRunner.getCurrentSchemaVersion());

        // Verify table exists
        assertTrue("Users table should exist", tableExists("users"));
    }

    @Test
    public void testDataPreservationDuringMigration() throws SQLException {
        // Setup: Create table and insert test data
        Statement stmt = connection.createStatement();
        stmt.execute("CREATE TABLE products (id INT PRIMARY KEY, name VARCHAR(100), price DECIMAL(10,2))");
        stmt.execute("INSERT INTO products VALUES (1, 'Laptop', 999.99)");
        stmt.execute("INSERT INTO products VALUES (2, 'Mouse', 29.99)");

        // Migration: Add new column with default value
        Migration addColumnMigration = new Migration(2,
            "ALTER TABLE products ADD COLUMN category VARCHAR(50) DEFAULT 'Electronics'",
            "ALTER TABLE products DROP COLUMN category");

        migrationRunner.applyMigration(addColumnMigration);

        // Verify data preserved and default applied
        ResultSet rs = stmt.executeQuery(
            "SELECT id, name, price, category FROM products ORDER BY id");

        assertTrue(rs.next());
        assertEquals(1, rs.getInt("id"));
        assertEquals("Laptop", rs.getString("name"));
        assertEquals(new BigDecimal("999.99"), rs.getBigDecimal("price"));
        assertEquals("Electronics", rs.getString("category"));

        assertTrue(rs.next());
        assertEquals("Mouse", rs.getString("name"));
        assertEquals("Electronics", rs.getString("category"));
    }

    @Test
    public void testRollbackMigration() throws SQLException {
        // Apply migration
        Migration migration = new Migration(3,
            "CREATE TABLE temp_table (id INT, data VARCHAR(100))",
            "DROP TABLE temp_table");

        migrationRunner.applyMigration(migration);
        assertTrue("Table should exist after migration", tableExists("temp_table"));

        // Rollback migration
        migrationRunner.rollbackMigration(3);
        assertFalse("Table should not exist after rollback", tableExists("temp_table"));

        // Verify version rolled back
        assertEquals("Schema version should be rolled back", 2, 
            migrationRunner.getCurrentSchemaVersion());
    }

    @Test
    public void testComplexDataMigration() throws SQLException {
        // Setup: Old table structure
        Statement stmt = connection.createStatement();
        stmt.execute("CREATE TABLE users_old (id INT PRIMARY KEY, full_name VARCHAR(200), email VARCHAR(100))");
        stmt.execute("INSERT INTO users_old VALUES (1, 'John Doe', 'john@example.com')");
        stmt.execute("INSERT INTO users_old VALUES (2, 'Jane Smith', 'jane@example.com')");

        // Migration: Split full_name into first_name and last_name
        String migrationSQL = """
            CREATE TABLE users_new (
                id INT PRIMARY KEY,
                first_name VARCHAR(100),
                last_name VARCHAR(100),
                email VARCHAR(100)
            );

            INSERT INTO users_new (id, first_name, last_name, email)
            SELECT 
                id,
                SUBSTRING(full_name, 1, CHARINDEX(' ', full_name) - 1) as first_name,
                SUBSTRING(full_name, CHARINDEX(' ', full_name) + 1, LEN(full_name)) as last_name,
                email
            FROM users_old;

            DROP TABLE users_old;

            ALTER TABLE users_new RENAME TO users;
        """;

        Migration complexMigration = new Migration(4, migrationSQL, 
            "-- Rollback would be complex, implement if needed");

        migrationRunner.applyMigration(complexMigration);

        // Verify data transformation
        ResultSet rs = stmt.executeQuery(
            "SELECT first_name, last_name, email FROM users WHERE id = 1");

        assertTrue(rs.next());
        assertEquals("John", rs.getString("first_name"));
        assertEquals("Doe", rs.getString("last_name"));
        assertEquals("john@example.com", rs.getString("email"));
    }

    @Test
    public void testMigrationPerformance() throws SQLException {
        // Create large dataset
        Statement stmt = connection.createStatement();
        stmt.execute("CREATE TABLE large_table (id INT PRIMARY KEY, data VARCHAR(1000))");

        // Insert 100K records
        PreparedStatement insertStmt = connection.prepareStatement(
            "INSERT INTO large_table (id, data) VALUES (?, ?)");

        connection.setAutoCommit(false);
        for (int i = 1; i <= 100000; i++) {
            insertStmt.setInt(1, i);
            insertStmt.setString(2, "Test data " + i);
            insertStmt.addBatch();

            if (i % 1000 == 0) {
                insertStmt.executeBatch();
            }
        }
        insertStmt.executeBatch();
        connection.commit();
        connection.setAutoCommit(true);

        // Test migration performance
        long startTime = System.currentTimeMillis();

        Migration indexMigration = new Migration(5,
            "CREATE INDEX idx_large_table_data ON large_table(data)",
            "DROP INDEX idx_large_table_data");

        migrationRunner.applyMigration(indexMigration);

        long migrationTime = System.currentTimeMillis() - startTime;

        // Performance assertion (adjust based on requirements)
        assertTrue("Migration should complete within 30 seconds", 
            migrationTime < 30000);

        // Verify index created
        assertTrue("Index should exist", indexExists("idx_large_table_data"));
    }

    private boolean tableExists(String tableName) throws SQLException {
        DatabaseMetaData metaData = connection.getMetaData();
        ResultSet tables = metaData.getTables(null, null, tableName.toUpperCase(), null);
        return tables.next();
    }

    private boolean indexExists(String indexName) throws SQLException {
        DatabaseMetaData metaData = connection.getMetaData();
        ResultSet indexes = metaData.getIndexInfo(null, null, null, false, false);
        while (indexes.next()) {
            if (indexName.equalsIgnoreCase(indexes.getString("INDEX_NAME"))) {
                return true;
            }
        }
        return false;
    }
}

