// Java code for performance test assertions
public class PerformanceTestAssertions {

    @Test
    public void testResponseTime() {
        long startTime = System.currentTimeMillis();

        // Execute API call or operation
        Response response = given()
            .when()
            .get("/api/users");

        long endTime = System.currentTimeMillis();
        long responseTime = endTime - startTime;

        // Performance assertions
        Assert.assertTrue("Response time should be under 2 seconds", 
            responseTime < 2000);
        Assert.assertEquals(200, response.getStatusCode());
    }

    @Test
    public void testThroughput() {
        int numberOfRequests = 100;
        long startTime = System.currentTimeMillis();

        for (int i = 0; i < numberOfRequests; i++) {
            given().when().get("/api/health");
        }

        long endTime = System.currentTimeMillis();
        long totalTime = endTime - startTime;
        double requestsPerSecond = (numberOfRequests * 1000.0) / totalTime;

        Assert.assertTrue("Throughput should be at least 10 requests/second", 
            requestsPerSecond >= 10.0);
    }
}

// Performance Metrics Analysis Framework
public class PerformanceMetricsAnalyzer {

    public static class PerformanceMetrics {
        private List<Long> responseTimes;
        private int totalRequests;
        private int successfulRequests;
        private int failedRequests;
        private long testDurationMs;
        private Map<String, Double> resourceUtilization;

        public PerformanceMetrics() {
            this.responseTimes = new ArrayList<>();
            this.resourceUtilization = new HashMap<>();
        }

        // Calculate key metrics
        public double getAverageResponseTime() {
            return responseTimes.stream().mapToLong(Long::longValue).average().orElse(0.0);
        }

        public long getMedianResponseTime() {
            List<Long> sorted = responseTimes.stream().sorted().collect(Collectors.toList());
            int size = sorted.size();
            if (size == 0) return 0;

            if (size % 2 == 0) {
                return (sorted.get(size/2 - 1) + sorted.get(size/2)) / 2;
            } else {
                return sorted.get(size/2);
            }
        }

        public long getPercentile(int percentile) {
            if (responseTimes.isEmpty()) return 0;

            List<Long> sorted = responseTimes.stream().sorted().collect(Collectors.toList());
            int index = (int) Math.ceil(percentile / 100.0 * sorted.size()) - 1;
            return sorted.get(Math.max(0, Math.min(index, sorted.size() - 1)));
        }

        public double getThroughput() {
            if (testDurationMs == 0) return 0;
            return (successfulRequests * 1000.0) / testDurationMs; // requests per second
        }

        public double getErrorRate() {
            if (totalRequests == 0) return 0;
            return (failedRequests * 100.0) / totalRequests; // percentage
        }

        public void addResponseTime(long responseTime) {
            responseTimes.add(responseTime);
        }

        public void recordSuccess() {
            successfulRequests++;
            totalRequests++;
        }

        public void recordFailure() {
            failedRequests++;
            totalRequests++;
        }

        public void setResourceUtilization(String resource, double utilization) {
            resourceUtilization.put(resource, utilization);
        }
    }

    @Test
    public void performLoadTestWithMetricsCollection() {
        PerformanceMetrics metrics = new PerformanceMetrics();
        long testStartTime = System.currentTimeMillis();

        // Simulate load test
        int numberOfUsers = 50;
        int requestsPerUser = 20;
        ExecutorService executor = Executors.newFixedThreadPool(numberOfUsers);
        CountDownLatch latch = new CountDownLatch(numberOfUsers * requestsPerUser);

        for (int user = 0; user < numberOfUsers; user++) {
            for (int request = 0; request < requestsPerUser; request++) {
                executor.submit(() -> {
                    try {
                        long requestStart = System.currentTimeMillis();

                        // Execute API call
                        Response response = given()
                            .when()
                            .get("/api/products");

                        long requestEnd = System.currentTimeMillis();
                        long responseTime = requestEnd - requestStart;

                        // Record metrics
                        synchronized (metrics) {
                            metrics.addResponseTime(responseTime);
                            if (response.getStatusCode() == 200) {
                                metrics.recordSuccess();
                            } else {
                                metrics.recordFailure();
                            }
                        }

                    } catch (Exception e) {
                        synchronized (metrics) {
                            metrics.recordFailure();
                        }
                    } finally {
                        latch.countDown();
                    }
                });
            }
        }

        try {
            latch.await(300, TimeUnit.SECONDS); // Wait for all requests
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }

        executor.shutdown();
        long testEndTime = System.currentTimeMillis();
        metrics.testDurationMs = testEndTime - testStartTime;

        // Simulate resource utilization (in real test, get from monitoring tools)
        metrics.setResourceUtilization("CPU", 65.5);
        metrics.setResourceUtilization("Memory", 78.2);
        metrics.setResourceUtilization("Disk I/O", 42.1);

        // Analyze and assert metrics
        analyzePerformanceMetrics(metrics);
    }

    private void analyzePerformanceMetrics(PerformanceMetrics metrics) {
        System.out.println("\n=== PERFORMANCE TEST RESULTS ===");

        // Response Time Analysis
        System.out.printf("Average Response Time: %.2f ms\n", metrics.getAverageResponseTime());
        System.out.printf("Median Response Time: %d ms\n", metrics.getMedianResponseTime());
        System.out.printf("90th Percentile: %d ms\n", metrics.getPercentile(90));
        System.out.printf("95th Percentile: %d ms\n", metrics.getPercentile(95));
        System.out.printf("99th Percentile: %d ms\n", metrics.getPercentile(99));

        // Throughput Analysis
        System.out.printf("Throughput: %.2f requests/second\n", metrics.getThroughput());
        System.out.printf("Total Requests: %d\n", metrics.totalRequests);
        System.out.printf("Successful Requests: %d\n", metrics.successfulRequests);
        System.out.printf("Failed Requests: %d\n", metrics.failedRequests);

        // Error Rate Analysis
        System.out.printf("Error Rate: %.2f%%\n", metrics.getErrorRate());

        // Resource Utilization
        System.out.println("\nResource Utilization:");
        metrics.resourceUtilization.forEach((resource, utilization) ->
            System.out.printf("%s: %.1f%%\n", resource, utilization));

        // Performance Assertions
        Assert.assertTrue("Average response time should be under 2 seconds", 
            metrics.getAverageResponseTime() < 2000);

        Assert.assertTrue("95th percentile should be under 5 seconds", 
            metrics.getPercentile(95) < 5000);

        Assert.assertTrue("Throughput should be at least 10 requests/second", 
            metrics.getThroughput() >= 10.0);

        Assert.assertTrue("Error rate should be less than 1%", 
            metrics.getErrorRate() < 1.0);

        Assert.assertTrue("CPU utilization should be under 80%", 
            metrics.resourceUtilization.get("CPU") < 80.0);

        Assert.assertTrue("Memory utilization should be under 85%", 
            metrics.resourceUtilization.get("Memory") < 85.0);

        // Performance Trend Analysis
        analyzePerformanceTrends(metrics);
    }

    private void analyzePerformanceTrends(PerformanceMetrics metrics) {
        System.out.println("\n=== PERFORMANCE ANALYSIS ===");

        // Response time distribution analysis
        long p50 = metrics.getMedianResponseTime();
        long p95 = metrics.getPercentile(95);
        long p99 = metrics.getPercentile(99);

        if (p95 > p50 * 3) {
            System.out.println("⚠️  WARNING: High response time variance detected");
            System.out.println("   Some requests are significantly slower than others");
        }

        if (p99 > p95 * 2) {
            System.out.println("⚠️  WARNING: Outlier responses detected");
            System.out.println("   1% of requests are taking much longer than expected");
        }

        // Throughput analysis
        double throughput = metrics.getThroughput();
        if (throughput < 5.0) {
            System.out.println("⚠️  WARNING: Low throughput detected");
            System.out.println("   System may be experiencing bottlenecks");
        }

        // Error rate analysis
        if (metrics.getErrorRate() > 0.1) {
            System.out.println("⚠️  WARNING: Elevated error rate detected");
            System.out.printf("   %.2f%% of requests are failing\n", metrics.getErrorRate());
        }

        // Resource utilization warnings
        metrics.resourceUtilization.forEach((resource, utilization) -> {
            if (utilization > 75.0) {
                System.out.printf("⚠️  WARNING: High %s utilization: %.1f%%\n", resource, utilization);
            }
        });

        System.out.println("\n=== RECOMMENDATIONS ===");
        if (p95 > 3000) {
            System.out.println("• Consider optimizing slow database queries");
            System.out.println("• Review application caching strategies");
        }

        if (metrics.resourceUtilization.get("CPU") > 70) {
            System.out.println("• Consider horizontal scaling or CPU optimization");
        }

        if (metrics.resourceUtilization.get("Memory") > 80) {
            System.out.println("• Investigate potential memory leaks");
            System.out.println("• Consider increasing memory allocation");
        }
    }
}

