                            // Run JMeter performance test
                            sh '''
                                ${JMETER_HOME}/bin/jmeter -n \
                                    -t tests/performance/api-load-test.jmx \
                                    -l ${TEST_RESULTS_DIR}/api-results.jtl \
                                    -e -o ${TEST_RESULTS_DIR}/api-dashboard \
                                    -Jhost=test-app \
                                    -Jport=8080 \
                                    -Jusers=50 \
                                    -Jrampup=300 \
                                    -Jduration=600
                            '''
                        }
                    }
                }

                stage('Database Performance Test') {
                    steps {
                        script {
                            // Run database-focused performance test
                            sh '''
                                ${JMETER_HOME}/bin/jmeter -n \
                                    -t tests/performance/db-stress-test.jmx \
                                    -l ${TEST_RESULTS_DIR}/db-results.jtl \
                                    -e -o ${TEST_RESULTS_DIR}/db-dashboard \
                                    -Jdb_host=test-db \
                                    -Jdb_port=5432 \
                                    -Jconcurrent_connections=20
                            '''
                        }
                    }
                }

                stage('UI Performance Test') {
                    steps {
                        script {
                            // Run Lighthouse CI for frontend performance
                            sh '''
                                npm install -g @lhci/cli@0.8.x
                                lhci collect --url=http://test-app:8080 \
                                    --settings.chromeFlags="--no-sandbox" \
                                    --numberOfRuns=3
                                lhci upload --target=filesystem \
                                    --outputDir=${TEST_RESULTS_DIR}/lighthouse
                            '''
                        }
                    }
                }
            }
        }

        stage('Performance Analysis') {
            steps {
                script {
                    // Parse JMeter results
                    def apiResults = parseJMeterResults("${TEST_RESULTS_DIR}/api-results.jtl")
                    def dbResults = parseJMeterResults("${TEST_RESULTS_DIR}/db-results.jtl")

                    // Load performance thresholds
                    def thresholds = readProperties file: PERFORMANCE_THRESHOLD_FILE

                    // Validate performance metrics
                    validatePerformanceMetrics(apiResults, thresholds, 'API')
                    validatePerformanceMetrics(dbResults, thresholds, 'Database')

                    // Generate performance report
                    generatePerformanceReport(apiResults, dbResults)

                    // Store results for trend analysis
                    storePerformanceResults(apiResults, dbResults)
                }
            }
        }

        stage('Performance Gate') {
            steps {
                script {
                    // Check if performance tests passed
                    def performanceGatePassed = checkPerformanceGate()

                    if (!performanceGatePassed) {
                        error "Performance tests failed. Stopping pipeline."
                    }

                    echo "Performance gate passed. Proceeding to deployment."
                }
            }
        }
    }

    post {
        always {
            // Archive performance test results
            archiveArtifacts artifacts: '${TEST_RESULTS_DIR}/**/*', allowEmptyArchive: true

            // Publish JMeter results
            publishHTML([
                allowMissing: false,
                alwaysLinkToLastBuild: true,
                keepAll: true,
                reportDir: '${TEST_RESULTS_DIR}/api-dashboard',
                reportFiles: 'index.html',
                reportName: 'API Performance Report'
            ])

            // Cleanup test environment
            sh 'docker-compose -f docker-compose.test.yml down'
        }

        failure {
            // Send notification on performance test failure
            emailext(
                subject: "Performance Test Failed: ${env.JOB_NAME} - ${env.BUILD_NUMBER}",
                body: '''Performance tests have failed for build ${BUILD_NUMBER}.
                         Check the performance reports for details.
                         Build URL: ${BUILD_URL}''',
                recipientProviders: [developers(), requestor()]
            )
        }
    }
}

// Performance validation functions
def parseJMeterResults(resultsFile) {
    def results = [:]

    // Use JMeter plugins or custom script to parse JTL file
    sh "python3 scripts/parse-jmeter-results.py ${resultsFile} > parsed-results.json"
    results = readJSON file: 'parsed-results.json'

    return results
}

def validatePerformanceMetrics(results, thresholds, testType) {
    def passed = true

    // Validate response time
    if (results.averageResponseTime > thresholds["${testType.toLowerCase()}.max.response.time"].toInteger()) {
        echo "❌ ${testType} average response time exceeded threshold: ${results.averageResponseTime}ms"
        passed = false
    }

    // Validate 95th percentile
    if (results.percentile95 > thresholds["${testType.toLowerCase()}.max.percentile95"].toInteger()) {
        echo "❌ ${testType} 95th percentile exceeded threshold: ${results.percentile95}ms"
        passed = false
    }

    // Validate throughput
    if (results.throughput < thresholds["${testType.toLowerCase()}.min.throughput"].toDouble()) {
        echo "❌ ${testType} throughput below threshold: ${results.throughput} req/s"
        passed = false
    }

    // Validate error rate
    if (results.errorRate > thresholds["${testType.toLowerCase()}.max.error.rate"].toDouble()) {
        echo "❌ ${testType} error rate exceeded threshold: ${results.errorRate}%"
        passed = false
    }

    if (passed) {
        echo "✅ ${testType} performance tests passed all thresholds"
    }

    return passed
}

def generatePerformanceReport(apiResults, dbResults) {
    // Generate comprehensive performance report
    def reportContent = """
    # Performance Test Report - Build ${env.BUILD_NUMBER}

    ## API Performance Results
    - Average Response Time: ${apiResults.averageResponseTime}ms
    - 95th Percentile: ${apiResults.percentile95}ms
    - Throughput: ${apiResults.throughput} req/s
    - Error Rate: ${apiResults.errorRate}%

    ## Database Performance Results
    - Average Response Time: ${dbResults.averageResponseTime}ms
    - 95th Percentile: ${dbResults.percentile95}ms
    - Throughput: ${dbResults.throughput} req/s
    - Error Rate: ${dbResults.errorRate}%

    ## Recommendations
    ${generateRecommendations(apiResults, dbResults)}
    """

    writeFile file: 'performance-report.md', text: reportContent
}

def storePerformanceResults(apiResults, dbResults) {
    // Store results in InfluxDB or similar for trend analysis
    sh '''
        curl -X POST "http://influxdb:8086/write?db=performance" \
        --data-binary "
            api_performance,build=${BUILD_NUMBER} \
                response_time=${apiResults.averageResponseTime}, \
                percentile_95=${apiResults.percentile95}, \
                throughput=${apiResults.throughput}, \
                error_rate=${apiResults.errorRate}

            db_performance,build=${BUILD_NUMBER} \
                response_time=${dbResults.averageResponseTime}, \
                percentile_95=${dbResults.percentile95}, \
                throughput=${dbResults.throughput}, \
                error_rate=${dbResults.errorRate}
        "
    '''
}

def checkPerformanceGate() {
    // Implement logic to determine if performance gate should pass
    def gateFile = 'performance-gate-result.txt'

    // This would contain the result of performance validation
    if (fileExists(gateFile)) {
        def gateResult = readFile(gateFile).trim()
        return gateResult == 'PASS'
    }

    return false
}

