// Scalability Testing Framework
public class ScalabilityTestSuite {

    // Configuration for different load levels
    private static final Map<String, LoadConfiguration> LOAD_LEVELS = Map.of(
        "BASELINE", new LoadConfiguration(10, 60, 300),   // 10 users, 1 min, 5 min
        "NORMAL", new LoadConfiguration(50, 300, 1800),   // 50 users, 5 min, 30 min
        "PEAK", new LoadConfiguration(200, 600, 3600),    // 200 users, 10 min, 1 hour
        "STRESS", new LoadConfiguration(500, 300, 1800),  // 500 users, 5 min, 30 min
        "EXTREME", new LoadConfiguration(1000, 180, 900)  // 1000 users, 3 min, 15 min
    );

    public static class LoadConfiguration {
        public final int users;
        public final int rampUpSeconds;
        public final int durationSeconds;

        public LoadConfiguration(int users, int rampUpSeconds, int durationSeconds) {
            this.users = users;
            this.rampUpSeconds = rampUpSeconds;
            this.durationSeconds = durationSeconds;
        }
    }

    public static class ScalabilityMetrics {
        private String loadLevel;
        private int concurrentUsers;
        private double averageResponseTime;
        private double throughput;
        private double errorRate;
        private Map<String, Double> resourceUtilization;
        private double cpuUtilization;
        private double memoryUtilization;
        private double diskIOUtilization;
        private double networkUtilization;

        // Constructor and getters
        public ScalabilityMetrics(String loadLevel, int concurrentUsers) {
            this.loadLevel = loadLevel;
            this.concurrentUsers = concurrentUsers;
            this.resourceUtilization = new HashMap<>();
        }

        // Calculate scalability efficiency
        public double calculateScalabilityEfficiency(ScalabilityMetrics baselineMetrics) {
            if (baselineMetrics.concurrentUsers == 0 || this.concurrentUsers == 0) {
                return 0.0;
            }

            double expectedThroughput = baselineMetrics.throughput * 
                ((double) this.concurrentUsers / baselineMetrics.concurrentUsers);

            return (this.throughput / expectedThroughput) * 100.0;
        }

        // Identify bottlenecks
        public List<String> identifyBottlenecks() {
            List<String> bottlenecks = new ArrayList<>();

            if (cpuUtilization > 80.0) {
                bottlenecks.add("CPU bottleneck detected: " + cpuUtilization + "% utilization");
            }

            if (memoryUtilization > 85.0) {
                bottlenecks.add("Memory bottleneck detected: " + memoryUtilization + "% utilization");
            }

            if (diskIOUtilization > 70.0) {
                bottlenecks.add("Disk I/O bottleneck detected: " + diskIOUtilization + "% utilization");
            }

            if (networkUtilization > 60.0) {
                bottlenecks.add("Network bottleneck detected: " + networkUtilization + "% utilization");
            }

            if (averageResponseTime > 5000) {
                bottlenecks.add("Response time bottleneck: " + averageResponseTime + "ms average");
            }

            if (errorRate > 5.0) {
                bottlenecks.add("Error rate bottleneck: " + errorRate + "% errors");
            }

            return bottlenecks;
        }
    }

    @Test
    public void testHorizontalScalability() {
        List<ScalabilityMetrics> results = new ArrayList<>();
        ScalabilityMetrics baselineMetrics = null;

        // Test each load level
        for (Map.Entry<String, LoadConfiguration> entry : LOAD_LEVELS.entrySet()) {
            String level = entry.getKey();
            LoadConfiguration config = entry.getValue();

            System.out.println("\n=== Testing " + level + " Load Level ===");
            System.out.println("Users: " + config.users + ", Ramp-up: " + config.rampUpSeconds + "s, Duration: " + config.durationSeconds + "s");

            ScalabilityMetrics metrics = executeLoadTest(level, config);
            results.add(metrics);

            if (level.equals("BASELINE")) {
                baselineMetrics = metrics;
            }

            // Print immediate results
            printMetrics(metrics);

            // Check for breaking point
            if (metrics.errorRate > 10.0 || metrics.averageResponseTime > 10000) {
                System.out.println("⚠️ Breaking point reached at " + level + " load level");
                break;
            }

            // Wait between tests
            try {
                Thread.sleep(30000); // 30 second cool-down
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }

        // Analyze scalability patterns
        analyzeScalabilityResults(results, baselineMetrics);

        // Generate capacity planning recommendations
        generateCapacityPlanningReport(results);
    }

    private ScalabilityMetrics executeLoadTest(String level, LoadConfiguration config) {
        ScalabilityMetrics metrics = new ScalabilityMetrics(level, config.users);

        // Simulate executing performance test
        // In real implementation, this would trigger JMeter, k6, or other tools

        List<Long> responseTimes = new ArrayList<>();
        int totalRequests = 0;
        int successfulRequests = 0;

        // Simulate load test execution
        long testStartTime = System.currentTimeMillis();
        ExecutorService executor = Executors.newFixedThreadPool(config.users);
        CountDownLatch latch = new CountDownLatch(config.users * 10); // 10 requests per user

        for (int user = 0; user < config.users; user++) {
            executor.submit(() -> {
                for (int request = 0; request < 10; request++) {
                    try {
                        long requestStart = System.currentTimeMillis();

                        // Simulate API call with increasing latency based on load
                        simulateAPICall(config.users);

                        long requestEnd = System.currentTimeMillis();
                        long responseTime = requestEnd - requestStart;

                        synchronized (responseTimes) {
                            responseTimes.add(responseTime);
                            successfulRequests++;
                        }

                    } catch (Exception e) {
                        // Request failed
                    } finally {
                        totalRequests++;
                        latch.countDown();
                    }
                }
            });
        }

        try {
            latch.await(config.durationSeconds + 60, TimeUnit.SECONDS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }

        executor.shutdown();
        long testEndTime = System.currentTimeMillis();
        long testDurationMs = testEndTime - testStartTime;

        // Calculate metrics
        metrics.averageResponseTime = responseTimes.stream()
            .mapToLong(Long::longValue)
            .average()
            .orElse(0.0);

        metrics.throughput = (successfulRequests * 1000.0) / testDurationMs;
        metrics.errorRate = ((totalRequests - successfulRequests) * 100.0) / totalRequests;

        // Simulate resource utilization (would come from monitoring in real scenario)
        metrics.cpuUtilization = Math.min(95.0, 30.0 + (config.users * 0.15));
        metrics.memoryUtilization = Math.min(90.0, 40.0 + (config.users * 0.12));
        metrics.diskIOUtilization = Math.min(80.0, 20.0 + (config.users * 0.08));
        metrics.networkUtilization = Math.min(75.0, 15.0 + (config.users * 0.06));

        return metrics;
    }

    private void simulateAPICall(int currentLoad) throws InterruptedException {
        // Simulate API call with degrading performance under load
        int baseLatency = 100;
        int additionalLatency = (int) (currentLoad * 0.5); // Each user adds 0.5ms
        int jitter = (int) (Math.random() * 50); // Random jitter

        Thread.sleep(baseLatency + additionalLatency + jitter);

        // Simulate occasional failures under high load
        if (currentLoad > 300 && Math.random() < 0.02) { // 2% failure rate at high load
            throw new RuntimeException("Simulated service failure");
        }
    }

    private void printMetrics(ScalabilityMetrics metrics) {
        System.out.printf("Users: %d\n", metrics.concurrentUsers);
        System.out.printf("Avg Response Time: %.2f ms\n", metrics.averageResponseTime);
        System.out.printf("Throughput: %.2f req/s\n", metrics.throughput);
        System.out.printf("Error Rate: %.2f%%\n", metrics.errorRate);
        System.out.printf("CPU: %.1f%%, Memory: %.1f%%, Disk I/O: %.1f%%, Network: %.1f%%\n",
            metrics.cpuUtilization, metrics.memoryUtilization, 
            metrics.diskIOUtilization, metrics.networkUtilization);

        List<String> bottlenecks = metrics.identifyBottlenecks();
        if (!bottlenecks.isEmpty()) {
            System.out.println("Bottlenecks:");
            bottlenecks.forEach(bottleneck -> System.out.println("  • " + bottleneck));
        }
    }

    private void analyzeScalabilityResults(List<ScalabilityMetrics> results, 
                                          ScalabilityMetrics baselineMetrics) {
        System.out.println("\n=== SCALABILITY ANALYSIS ===");

        for (ScalabilityMetrics metrics : results) {
            if (baselineMetrics != null && !metrics.loadLevel.equals("BASELINE")) {
                double efficiency = metrics.calculateScalabilityEfficiency(baselineMetrics);
                System.out.printf("%s Load: %.1f%% scalability efficiency\n", 
                    metrics.loadLevel, efficiency);

                if (efficiency < 70.0) {
                    System.out.printf("  ⚠️ Poor scalability at %s level (%.1f%% efficiency)\n", 
                        metrics.loadLevel, efficiency);
                }
            }
        }

        // Find optimal operating point
        findOptimalOperatingPoint(results);
    }

    private void findOptimalOperatingPoint(List<ScalabilityMetrics> results) {
        System.out.println("\n=== OPTIMAL OPERATING POINT ===");

        ScalabilityMetrics optimalPoint = null;
        double bestScore = 0.0;

        for (ScalabilityMetrics metrics : results) {
            // Score based on throughput, response time, and error rate
            double score = metrics.throughput / 
                (1 + metrics.averageResponseTime / 1000.0 + metrics.errorRate);

            if (score > bestScore && metrics.errorRate < 5.0 && metrics.averageResponseTime < 3000) {
                bestScore = score;
                optimalPoint = metrics;
            }
        }

        if (optimalPoint != null) {
            System.out.printf("Optimal point: %s load level\n", optimalPoint.loadLevel);
            System.out.printf("  Users: %d\n", optimalPoint.concurrentUsers);
            System.out.printf("  Throughput: %.2f req/s\n", optimalPoint.throughput);
            System.out.printf("  Response Time: %.2f ms\n", optimalPoint.averageResponseTime);
            System.out.printf("  Error Rate: %.2f%%\n", optimalPoint.errorRate);
        }
    }

    private void generateCapacityPlanningReport(List<ScalabilityMetrics> results) {
        System.out.println("\n=== CAPACITY PLANNING RECOMMENDATIONS ===");

        // Find the maximum sustainable load
        ScalabilityMetrics maxSustainableLoad = results.stream()
            .filter(m -> m.errorRate < 1.0 && m.averageResponseTime < 5000)
            .max(Comparator.comparing(m -> m.concurrentUsers))
            .orElse(results.get(0));

        System.out.printf("Maximum sustainable load: %d concurrent users\n", 
            maxSustainableLoad.concurrentUsers);

        // Growth projections
        int[] growthProjections = {150, 200, 300}; // 50%, 100%, 200% growth

        for (int growthPercent : growthProjections) {
            int projectedUsers = (int) (maxSustainableLoad.concurrentUsers * (growthPercent / 100.0));

            System.out.printf("\nFor %d%% growth (%d users):\n", growthPercent, projectedUsers);

            if (projectedUsers > maxSustainableLoad.concurrentUsers) {
                System.out.println("  ⚠️ Infrastructure scaling required");

                // Estimate resource requirements
                double cpuScalingFactor = (double) projectedUsers / maxSustainableLoad.concurrentUsers;
                double memoryScalingFactor = Math.sqrt(cpuScalingFactor); // Memory scales sub-linearly

                System.out.printf("  Recommended CPU scaling: %.1fx\n", cpuScalingFactor);
                System.out.printf("  Recommended memory scaling: %.1fx\n", memoryScalingFactor);

                if (cpuScalingFactor > 2.0) {
                    System.out.println("  Consider horizontal scaling (more instances)");
                } else {
                    System.out.println("  Vertical scaling (larger instances) may suffice");
                }
            } else {
                System.out.println("  ✅ Current infrastructure can handle this growth");
            }
        }

        // Performance optimization recommendations
        System.out.println("\n=== OPTIMIZATION RECOMMENDATIONS ===");

        if (maxSustainableLoad.averageResponseTime > 1000) {
            System.out.println("• Optimize database queries and add caching");
        }

        if (maxSustainableLoad.cpuUtilization > 60) {
            System.out.println("• Optimize CPU-intensive operations");
            System.out.println("• Consider asynchronous processing for heavy tasks");
        }

        if (maxSustainableLoad.memoryUtilization > 70) {
            System.out.println("• Investigate memory usage patterns");
            System.out.println("• Optimize object allocation and garbage collection");
        }

        System.out.println("• Implement auto-scaling based on CPU and memory thresholds");
        System.out.println("• Set up comprehensive monitoring and alerting");
        System.out.println("• Regular performance testing with each release");
    }
}

