public class AndroidAppiumSetup {
    private AndroidDriver driver;

    @BeforeClass
    public void setupAndroidEnvironment() throws Exception {
        // Verify Android environment
        verifyAndroidSDK();
        setupAndroidEmulator();
        installTestApp();
    }

    @BeforeMethod
    public void initializeAndroidDriver() throws MalformedURLException {
        DesiredCapabilities caps = new DesiredCapabilities();

        // Platform capabilities
        caps.setCapability("platformName", "Android");
        caps.setCapability("platformVersion", "12.0");
        caps.setCapability("deviceName", "Pixel_6_API_31");

        // App capabilities
        caps.setCapability("app", System.getProperty("user.dir") + "/apps/ApiDemos.apk");
        caps.setCapability("appPackage", "io.appium.android.apis");
        caps.setCapability("appActivity", "io.appium.android.apis.ApiDemos");

        // Automation capabilities
        caps.setCapability("automationName", "UiAutomator2");
        caps.setCapability("newCommandTimeout", 60);
        caps.setCapability("noReset", false);
        caps.setCapability("fullReset", false);

        // Performance capabilities
        caps.setCapability("skipServerInstallation", true);
        caps.setCapability("skipDeviceInitialization", true);
        caps.setCapability("unicodeKeyboard", true);
        caps.setCapability("resetKeyboard", true);

        // Create driver instance
        driver = new AndroidDriver(
            new URL("http://localhost:4723"), caps
        );

        // Set implicit wait
        driver.manage().timeouts().implicitlyWait(Duration.ofSeconds(10));
    }

    private void verifyAndroidSDK() {
        String androidHome = System.getenv("ANDROID_HOME");
        Assert.assertNotNull("ANDROID_HOME environment variable not set", androidHome);

        String adbPath = androidHome + "/platform-tools/adb";
        Assert.assertTrue("ADB not found at: " + adbPath, new File(adbPath).exists());

        System.out.println("✅ Android SDK verified");
    }

    private void setupAndroidEmulator() {
        try {
            // Check if emulator is running
            Process process = Runtime.getRuntime().exec("adb devices");
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            boolean emulatorFound = false;

            while ((line = reader.readLine()) != null) {
                if (line.contains("emulator") && line.contains("device")) {
                    emulatorFound = true;
                    break;
                }
            }

            if (!emulatorFound) {
                // Start emulator if not running
                System.out.println("Starting Android emulator...");
                Runtime.getRuntime().exec("emulator -avd Pixel_6_API_31 -no-audio -no-window");
                Thread.sleep(60000); // Wait for emulator to boot
            }

            System.out.println("✅ Android emulator ready");

        } catch (Exception e) {
            throw new RuntimeException("Failed to setup Android emulator: " + e.getMessage());
        }
    }
}

