public class IOSAppiumSetup {
    private IOSDriver driver;

    @BeforeMethod
    public void initializeIOSDriver() throws MalformedURLException {
        DesiredCapabilities caps = new DesiredCapabilities();

        // Platform capabilities
        caps.setCapability("platformName", "iOS");
        caps.setCapability("platformVersion", "15.5");
        caps.setCapability("deviceName", "iPhone 13");

        // App capabilities
        caps.setCapability("app", System.getProperty("user.dir") + "/apps/TestApp.app");
        caps.setCapability("bundleId", "com.example.testapp");

        // Automation capabilities
        caps.setCapability("automationName", "XCUITest");
        caps.setCapability("newCommandTimeout", 60);
        caps.setCapability("wdaLaunchTimeout", 120000);
        caps.setCapability("wdaConnectionTimeout", 120000);

        // iOS specific capabilities
        caps.setCapability("xcodeOrgId", "YOUR_TEAM_ID");
        caps.setCapability("xcodeSigningId", "iPhone Developer");
        caps.setCapability("updatedWDABundleId", "com.example.WebDriverAgentRunner");
        caps.setCapability("useNewWDA", false);
        caps.setCapability("useJSONSource", true);

        // Simulator capabilities
        caps.setCapability("isHeadless", false);
        caps.setCapability("connectHardwareKeyboard", false);

        driver = new IOSDriver(
            new URL("http://localhost:4723"), caps
        );

        driver.manage().timeouts().implicitlyWait(Duration.ofSeconds(10));
    }

    @BeforeClass
    public void setupIOSEnvironment() {
        verifyXcodeSetup();
        verifyIOSSimulator();
        setupWebDriverAgent();
    }

    private void verifyXcodeSetup() {
        try {
            Process process = Runtime.getRuntime().exec("xcode-select --print-path");
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String xcodePath = reader.readLine();

            Assert.assertNotNull("Xcode not installed", xcodePath);
            System.out.println("✅ Xcode found at: " + xcodePath);

            // Verify command line tools
            process = Runtime.getRuntime().exec("xcodebuild -version");
            reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String version = reader.readLine();
            System.out.println("✅ Xcode version: " + version);

        } catch (Exception e) {
            throw new RuntimeException("Xcode verification failed: " + e.getMessage());
        }
    }

    private void verifyIOSSimulator() {
        try {
            Process process = Runtime.getRuntime().exec("xcrun simctl list devices available");
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            boolean simulatorFound = false;

            while ((line = reader.readLine()) != null) {
                if (line.contains("iPhone 13") && line.contains("15.5")) {
                    simulatorFound = true;
                    break;
                }
            }

            Assert.assertTrue("iPhone 13 (15.5) simulator not found", simulatorFound);
            System.out.println("✅ iOS Simulator verified");

        } catch (Exception e) {
            throw new RuntimeException("iOS Simulator verification failed: " + e.getMessage());
        }
    }

    private void setupWebDriverAgent() {
        try {
            // Build WebDriverAgent (required for iOS automation)
            String wdaPath = "/usr/local/lib/node_modules/appium/node_modules/appium-webdriveragent";

            ProcessBuilder pb = new ProcessBuilder(
                "xcodebuild", "-project", "WebDriverAgent.xcodeproj",
                "-scheme", "WebDriverAgentRunner",
                "-destination", "platform=iOS Simulator,name=iPhone 13,OS=15.5",
                "test"
            );
            pb.directory(new File(wdaPath));

            System.out.println("🔨 Building WebDriverAgent...");
            Process process = pb.start();

            // Wait for build to complete (simplified)
            Thread.sleep(30000);

            System.out.println("✅ WebDriverAgent setup complete");

        } catch (Exception e) {
            System.out.println("⚠️ WebDriverAgent setup warning: " + e.getMessage());
        }
    }
}

// 4. Cross-Platform Driver Factory
public class MobileDriverFactory {

    public static AppiumDriver createDriver(String platform) throws MalformedURLException {
        DesiredCapabilities caps = new DesiredCapabilities();

        switch (platform.toLowerCase()) {
            case "android":
                return createAndroidDriver(caps);
            case "ios":
                return createIOSDriver(caps);
            default:
                throw new IllegalArgumentException("Platform not supported: " + platform);
        }
    }

    private static AndroidDriver createAndroidDriver(DesiredCapabilities caps) throws MalformedURLException {
        caps.setCapability("platformName", "Android");
        caps.setCapability("automationName", "UiAutomator2");
        // Add other Android-specific capabilities

        return new AndroidDriver(new URL("http://localhost:4723"), caps);
    }

    private static IOSDriver createIOSDriver(DesiredCapabilities caps) throws MalformedURLException {
        caps.setCapability("platformName", "iOS");
        caps.setCapability("automationName", "XCUITest");
        // Add other iOS-specific capabilities

        return new IOSDriver(new URL("http://localhost:4723"), caps);
    }
}

// 5. Appium Server Management
public class AppiumServerManager {
    private AppiumService service;

    public void startAppiumServer() {
        AppiumServiceBuilder builder = new AppiumServiceBuilder()
            .withAppiumJS(new File("/usr/local/bin/appium"))
            .withIPAddress("127.0.0.1")
            .usingPort(4723)
            .withArgument(() -> "--relaxed-security")
            .withArgument(() -> "--session-override")
            .withLogFile(new File("appium.log"));

        service = AppiumDriverLocalService.buildService(builder);
        service.start();

        System.out.println("🚀 Appium Server started at: " + service.getUrl());
    }

    public void stopAppiumServer() {
        if (service != null && service.isRunning()) {
            service.stop();
            System.out.println("🛑 Appium Server stopped");
        }
    }
}

// Mobile Locator Strategies Comprehensive Guide

public class MobileLocatorStrategies {
    private AppiumDriver driver;

    // 1. Android-Specific Locators
    @Test
    public void testAndroidLocators() {
        // Resource ID (Android preferred method)
        WebElement usernameField = driver.findElement(
            By.id("com.example.app:id/username")
        );

        // Class Name
        WebElement editText = driver.findElement(
            By.className("android.widget.EditText")
        );

        // UiSelector with UiAutomator (Android UiAutomator2)
        WebElement elementByText = driver.findElement(
            MobileBy.AndroidUIAutomator("new UiSelector().text(\"Login\")")
        );

        WebElement elementByDescription = driver.findElement(
            MobileBy.AndroidUIAutomator("new UiSelector().description(\"Login Button\")")
        );

        WebElement elementByResourceId = driver.findElement(
            MobileBy.AndroidUIAutomator("new UiSelector().resourceId(\"com.example.app:id/login_btn\")")
        );

        // Complex UiSelector combinations
        WebElement complexElement = driver.findElement(
            MobileBy.AndroidUIAutomator(
                "new UiSelector().className(\"android.widget.Button\")" +
                ".textContains(\"Submit\").enabled(true)"
            )
        );

        // UiScrollable for scrolling to elements
        WebElement scrollableElement = driver.findElement(
            MobileBy.AndroidUIAutomator(
                "new UiScrollable(new UiSelector().scrollable(true))" +
                ".scrollIntoView(new UiSelector().text(\"Target Text\"))"
            )
        );

        // Xpath (use sparingly for performance)
        WebElement xpathElement = driver.findElement(
            By.xpath("//android.widget.Button[@text='Click Me']")
        );

        usernameField.sendKeys("testuser");
        elementByText.click();
    }

    // 2. iOS-Specific Locators
    @Test
    public void testIOSLocators() {
        // Accessibility ID (iOS preferred method)
        WebElement button = driver.findElement(
            MobileBy.AccessibilityId("loginButton")
        );

        // Class Chain (iOS specific)
        WebElement elementByClassChain = driver.findElement(
            MobileBy.iOSClassChain("**/XCUIElementTypeButton[`name == 'Login'`]")
        );

        // Complex Class Chain
        WebElement nestedElement = driver.findElement(
            MobileBy.iOSClassChain(
                "**/XCUIElementTypeNavigationBar/XCUIElementTypeButton[2]" +
                "/XCUIElementTypeStaticText"
            )
        );

        // NSPredicate (iOS specific)
        WebElement predicateElement = driver.findElement(
            MobileBy.iOSNsPredicateString("name == 'Submit' AND enabled == 1")
        );

        // Complex NSPredicate
        WebElement complexPredicate = driver.findElement(
            MobileBy.iOSNsPredicateString(
                "type == 'XCUIElementTypeButton' AND name CONTAINS 'Save'"
            )
        );

        // Name (iOS)
        WebElement namedElement = driver.findElement(
            By.name("username_field")
        );

        // XPath (iOS)
        WebElement xpathElement = driver.findElement(
            By.xpath("//XCUIElementTypeButton[@name='Login']")
        );

        button.click();
        elementByClassChain.sendKeys("test");
    }

    // 3. Cross-Platform Locators
    @Test
    public void testCrossPlatformLocators() {
        // Accessibility ID (works on both platforms)
        WebElement crossPlatformButton = driver.findElement(
            MobileBy.AccessibilityId("submit_button")
        );

        // Tag Name (limited use)
        List<WebElement> allButtons = driver.findElements(
            By.tagName("button")
        );

        // XPath (performance impact, use carefully)
        WebElement xpathButton = driver.findElement(
            By.xpath("//*/[@text='Submit' or @name='Submit']")
        );

        crossPlatformButton.click();
    }

    // 4. Dynamic Element Location
    public class DynamicElementLocator {

        public WebElement findElementWithDynamicText(String dynamicText) {
            if (isAndroid()) {
                return driver.findElement(
                    MobileBy.AndroidUIAutomator(
                        String.format("new UiSelector().textContains(\"%s\")", dynamicText)
                    )
                );
            } else {
                return driver.findElement(
                    MobileBy.iOSNsPredicateString(
                        String.format("name CONTAINS '%s'", dynamicText)
                    )
                );
            }
        }

        public WebElement findElementByIndex(int index) {
            if (isAndroid()) {
                return driver.findElement(
                    MobileBy.AndroidUIAutomator(
                        String.format("new UiSelector().className(\"android.widget.Button\").instance(%d)", index)
                    )
                );
            } else {
                return driver.findElement(
                    MobileBy.iOSClassChain(
                        String.format("**/XCUIElementTypeButton[%d]", index + 1)
                    )
                );
            }
        }

        public List<WebElement> findElementsInContainer(String containerId) {
            if (isAndroid()) {
                return driver.findElements(
                    MobileBy.AndroidUIAutomator(
                        String.format(
                            "new UiSelector().resourceId(\"%s\").childSelector(" +
                            "new UiSelector().clickable(true))", containerId
                        )
                    )
                );
            } else {
                WebElement container = driver.findElement(MobileBy.AccessibilityId(containerId));
                return container.findElements(By.xpath(".//*[@enabled='true']"));
            }
        }

        private boolean isAndroid() {
            return driver.getCapabilities().getCapability("platformName").toString().equalsIgnoreCase("Android");
        }
    }

    // 5. Advanced Element Finding Techniques
    public class AdvancedElementFinder {

        public WebElement waitAndFindElement(By locator, int timeoutSeconds) {
            WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(timeoutSeconds));
            return wait.until(ExpectedConditions.presenceOfElementLocated(locator));
        }

        public WebElement findElementWithRetry(By locator, int maxRetries) {
            for (int i = 0; i < maxRetries; i++) {
                try {
                    return driver.findElement(locator);
                } catch (NoSuchElementException e) {
                    if (i == maxRetries - 1) {
                        throw e;
                    }
                    try {
                        Thread.sleep(1000);
                    } catch (InterruptedException ie) {
                        Thread.currentThread().interrupt();
                    }
                }
            }
            return null;
        }

        public WebElement findVisibleElement(By locator) {
            List<WebElement> elements = driver.findElements(locator);
            for (WebElement element : elements) {
                if (element.isDisplayed()) {
                    return element;
                }
            }
            throw new NoSuchElementException("No visible element found for: " + locator);
        }

        public WebElement scrollToElement(String text) {
            if (isAndroid()) {
                return driver.findElement(
                    MobileBy.AndroidUIAutomator(
                        "new UiScrollable(new UiSelector().scrollable(true))" +
                        ".scrollIntoView(new UiSelector().textContains(\"" + text + "\"))"
                    )
                );
            } else {
                // iOS scrolling implementation
                return scrollToElementIOS(text);
            }
        }

        private WebElement scrollToElementIOS(String text) {
            // iOS scroll implementation
            for (int i = 0; i < 10; i++) {
                try {
                    return driver.findElement(MobileBy.AccessibilityId(text));
                } catch (NoSuchElementException e) {
                    // Perform scroll gesture
                    Dimension size = driver.manage().window().getSize();
                    int startX = size.width / 2;
                    int startY = size.height * 3 / 4;
                    int endY = size.height / 4;

                    TouchAction touchAction = new TouchAction(driver);
                    touchAction.press(PointOption.point(startX, startY))
                              .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                              .moveTo(PointOption.point(startX, endY))
                              .release()
                              .perform();
                }
            }
            throw new NoSuchElementException("Element not found after scrolling: " + text);
        }
    }

    // 6. Page Object Model for Mobile
    public class MobileLoginPage {
        private AppiumDriver driver;

        // Platform-agnostic locators using accessibility IDs
        private By usernameField = MobileBy.AccessibilityId("username_input");
        private By passwordField = MobileBy.AccessibilityId("password_input");
        private By loginButton = MobileBy.AccessibilityId("login_button");

        // Platform-specific locators
        private By androidSpecificElement = MobileBy.AndroidUIAutomator(
