}

// Mobile Gestures and Touch Actions Comprehensive Guide

public class MobileGesturesTest {
    private AppiumDriver driver;
    private TouchAction touchAction;
    private Dimension screenSize;

    @BeforeMethod
    public void setUp() {
        touchAction = new TouchAction(driver);
        screenSize = driver.manage().window().getSize();
    }

    // 1. Basic Touch Actions
    @Test
    public void testBasicTouchActions() {
        WebElement element = driver.findElement(By.id("button1"));

        // Simple Tap
        touchAction.tap(PointOption.point(element.getLocation().x + element.getSize().width/2,
                                         element.getLocation().y + element.getSize().height/2))
                   .perform();

        // Alternative: Tap on element directly
        touchAction.tap(TapOptions.tapOptions().withElement(ElementOption.element(element)))
                   .perform();

        // Long Press
        touchAction.longPress(LongPressOptions.longPressOptions()
                             .withElement(ElementOption.element(element))
                             .withDuration(Duration.ofSeconds(2)))
                   .release()
                   .perform();

        // Press and Hold
        touchAction.press(ElementOption.element(element))
                   .waitAction(WaitOptions.waitOptions(Duration.ofSeconds(3)))
                   .release()
                   .perform();
    }

    // 2. Swipe Gestures
    @Test
    public void testSwipeGestures() {
        int centerX = screenSize.width / 2;
        int centerY = screenSize.height / 2;
        int edge = 10;

        // Swipe Right (Horizontal)
        swipeRight();

        // Swipe Left
        swipeLeft();

        // Swipe Up (Vertical)
        swipeUp();

        // Swipe Down
        swipeDown();

        // Diagonal Swipe
        touchAction.press(PointOption.point(edge, screenSize.height - edge))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                   .moveTo(PointOption.point(screenSize.width - edge, edge))
                   .release()
                   .perform();
    }

    private void swipeRight() {
        int startX = 50;
        int endX = screenSize.width - 50;
        int y = screenSize.height / 2;

        touchAction.press(PointOption.point(startX, y))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                   .moveTo(PointOption.point(endX, y))
                   .release()
                   .perform();
    }

    private void swipeLeft() {
        int startX = screenSize.width - 50;
        int endX = 50;
        int y = screenSize.height / 2;

        touchAction.press(PointOption.point(startX, y))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                   .moveTo(PointOption.point(endX, y))
                   .release()
                   .perform();
    }

    private void swipeUp() {
        int x = screenSize.width / 2;
        int startY = screenSize.height - 100;
        int endY = 100;

        touchAction.press(PointOption.point(x, startY))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                   .moveTo(PointOption.point(x, endY))
                   .release()
                   .perform();
    }

    private void swipeDown() {
        int x = screenSize.width / 2;
        int startY = 100;
        int endY = screenSize.height - 100;

        touchAction.press(PointOption.point(x, startY))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                   .moveTo(PointOption.point(x, endY))
                   .release()
                   .perform();
    }

    // 3. Multi-Touch Gestures (Pinch and Zoom)
    @Test
    public void testPinchAndZoom() {
        // Pinch to Zoom In
        performPinchToZoomIn();

        // Pinch to Zoom Out  
        performPinchToZoomOut();
    }

    private void performPinchToZoomIn() {
        int centerX = screenSize.width / 2;
        int centerY = screenSize.height / 2;

        // Create two touch actions for pinch
        TouchAction finger1 = new TouchAction(driver);
        TouchAction finger2 = new TouchAction(driver);

        // Starting positions (close together)
        finger1.press(PointOption.point(centerX - 50, centerY - 50))
               .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
               .moveTo(PointOption.point(centerX - 150, centerY - 150))
               .release();

        finger2.press(PointOption.point(centerX + 50, centerY + 50))
               .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
               .moveTo(PointOption.point(centerX + 150, centerY + 150))
               .release();

        // Perform multi-touch action
        MultiTouchAction multiTouch = new MultiTouchAction(driver);
        multiTouch.add(finger1).add(finger2).perform();
    }

    private void performPinchToZoomOut() {
        int centerX = screenSize.width / 2;
        int centerY = screenSize.height / 2;

        TouchAction finger1 = new TouchAction(driver);
        TouchAction finger2 = new TouchAction(driver);

        // Starting positions (far apart)
        finger1.press(PointOption.point(centerX - 150, centerY - 150))
               .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
               .moveTo(PointOption.point(centerX - 50, centerY - 50))
               .release();

        finger2.press(PointOption.point(centerX + 150, centerY + 150))
               .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
               .moveTo(PointOption.point(centerX + 50, centerY + 50))
               .release();

        MultiTouchAction multiTouch = new MultiTouchAction(driver);
        multiTouch.add(finger1).add(finger2).perform();
    }

    // 4. Advanced Scrolling Techniques
    @Test
    public void testAdvancedScrolling() {
        // Scroll to find element
        scrollToElement("Target Element");

        // Horizontal scroll (carousel)
        scrollHorizontalCarousel();

        // Infinite scroll handling
        handleInfiniteScroll();

        // Custom scroll with momentum
        performMomentumScroll();
    }

    private void scrollToElement(String elementText) {
        boolean found = false;
        int maxScrolls = 10;
        int scrollCount = 0;

        while (!found && scrollCount < maxScrolls) {
            try {
                driver.findElement(By.xpath(String.format("//*[@text='%s']", elementText)));
                found = true;
            } catch (NoSuchElementException e) {
                // Scroll down and try again
                swipeUp();
                scrollCount++;
            }
        }

        if (!found) {
            throw new RuntimeException("Element not found after " + maxScrolls + " scrolls: " + elementText);
        }
    }

    private void scrollHorizontalCarousel() {
        // Find carousel container
        WebElement carousel = driver.findElement(By.id("carousel_container"));

        int centerY = carousel.getLocation().y + carousel.getSize().height / 2;
        int startX = carousel.getLocation().x + carousel.getSize().width - 20;
        int endX = carousel.getLocation().x + 20;

        touchAction.press(PointOption.point(startX, centerY))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(300)))
                   .moveTo(PointOption.point(endX, centerY))
                   .release()
                   .perform();
    }

    private void handleInfiniteScroll() {
        List<WebElement> previousItems = new ArrayList<>();
        List<WebElement> currentItems;
        int unchangedCount = 0;

        do {
            currentItems = driver.findElements(By.className("list_item"));

            if (currentItems.equals(previousItems)) {
                unchangedCount++;
            } else {
                unchangedCount = 0;
            }

            previousItems = new ArrayList<>(currentItems);

            // Scroll to load more
            swipeUp();

        } while (unchangedCount < 3); // Stop if no new items loaded after 3 attempts
    }

    private void performMomentumScroll() {
        // Quick swipe with momentum
        int centerX = screenSize.width / 2;
        int startY = screenSize.height * 3 / 4;
        int endY = screenSize.height / 4;

        touchAction.press(PointOption.point(centerX, startY))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(100))) // Quick action
                   .moveTo(PointOption.point(centerX, endY))
                   .release()
                   .perform();
    }

    // 5. Drag and Drop
    @Test
    public void testDragAndDrop() {
        WebElement source = driver.findElement(By.id("draggable_item"));
        WebElement target = driver.findElement(By.id("drop_zone"));

        // Method 1: Using TouchAction
        dragAndDropWithTouchAction(source, target);

        // Method 2: Using Actions class (if supported)
        dragAndDropWithActions(source, target);
    }

    private void dragAndDropWithTouchAction(WebElement source, WebElement target) {
        int sourceX = source.getLocation().x + source.getSize().width / 2;
        int sourceY = source.getLocation().y + source.getSize().height / 2;
        int targetX = target.getLocation().x + target.getSize().width / 2;
        int targetY = target.getLocation().y + target.getSize().height / 2;

        touchAction.longPress(PointOption.point(sourceX, sourceY))
                   .waitAction(WaitOptions.waitOptions(Duration.ofMillis(1000)))
                   .moveTo(PointOption.point(targetX, targetY))
                   .release()
                   .perform();
    }

    private void dragAndDropWithActions(WebElement source, WebElement target) {
        Actions actions = new Actions(driver);
        actions.dragAndDrop(source, target).perform();
    }

    // 6. Device-Specific Gestures
    @Test
    public void testDeviceSpecificGestures() {
        if (isAndroid()) {
            testAndroidSpecificGestures();
        } else {
            testIOSSpecificGestures();
        }
    }

