    }

    // 7. Gesture Utils Class
    public static class GestureUtils {

        public static void scrollToBottom(AppiumDriver driver) {
            Dimension size = driver.manage().window().getSize();
            int centerX = size.width / 2;

            TouchAction touchAction = new TouchAction(driver);
            touchAction.press(PointOption.point(centerX, size.height - 100))
                       .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                       .moveTo(PointOption.point(centerX, 100))
                       .release()
                       .perform();
        }

        public static void scrollToTop(AppiumDriver driver) {
            Dimension size = driver.manage().window().getSize();
            int centerX = size.width / 2;

            TouchAction touchAction = new TouchAction(driver);
            touchAction.press(PointOption.point(centerX, 100))
                       .waitAction(WaitOptions.waitOptions(Duration.ofMillis(500)))
                       .moveTo(PointOption.point(centerX, size.height - 100))
                       .release()
                       .perform();
        }

        public static void doubleTap(AppiumDriver driver, WebElement element) {
            TouchAction touchAction = new TouchAction(driver);
            touchAction.tap(TapOptions.tapOptions().withElement(ElementOption.element(element)))
                       .waitAction(WaitOptions.waitOptions(Duration.ofMillis(100)))
                       .tap(TapOptions.tapOptions().withElement(ElementOption.element(element)))
                       .perform();
        }

        public static void tapAtCoordinates(AppiumDriver driver, int x, int y) {
            TouchAction touchAction = new TouchAction(driver);
            touchAction.tap(PointOption.point(x, y)).perform();
        }
    }

    private boolean isAndroid() {
        return driver.getCapabilities().getCapability("platformName").toString().equalsIgnoreCase("Android");
