    private void testOpenPorts() {
        String targetHost = "application.example.com";

        // Define expected open ports
        Set<Integer> expectedPorts = Set.of(80, 443, 22);
        Set<Integer> unexpectedPorts = Set.of(21, 23, 53, 135, 139, 445, 1433, 3306, 5432, 6379);

        // Use Nmap via system command (or Java network libraries)
        Map<Integer, String> openPorts = scanOpenPorts(targetHost);

        System.out.println("Open ports found: " + openPorts.keySet());

        // Verify expected ports are open
        for (Integer expectedPort : expectedPorts) {
            assertTrue("Expected port " + expectedPort + " should be open", 
                      openPorts.containsKey(expectedPort));
        }

        // Verify unexpected ports are closed
        for (Integer unexpectedPort : unexpectedPorts) {
            assertFalse("Unexpected port " + unexpectedPort + " should be closed", 
                       openPorts.containsKey(unexpectedPort));
        }

        // Check for dangerous services
        openPorts.forEach((port, service) -> {
            switch (port) {
                case 21:
                    fail("FTP service detected on port 21 - security risk");
                    break;
                case 23:
                    fail("Telnet service detected on port 23 - security risk");
                    break;
                case 135:
                case 139:
                case 445:
                    fail("Windows file sharing ports open - security risk: " + port);
                    break;
                case 1433:
                    fail("SQL Server port 1433 exposed - security risk");
                    break;
                case 3306:
                    fail("MySQL port 3306 exposed - security risk");
                    break;
                case 5432:
                    fail("PostgreSQL port 5432 exposed - security risk");
                    break;
            }
        });
    }

    private Map<Integer, String> scanOpenPorts(String host) {
        Map<Integer, String> openPorts = new HashMap<>();

        try {
            // Execute nmap command
            ProcessBuilder pb = new ProcessBuilder(
                "nmap", "-sS", "-O", "-sV", "-T4", 
                "--max-retries", "1",
                "--max-scan-delay", "5s",
                host
            );

            Process process = pb.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));

            String line;
            while ((line = reader.readLine()) != null) {
                // Parse nmap output
                if (line.contains("/tcp") && line.contains("open")) {
                    String[] parts = line.trim().split("\\s+");
                    if (parts.length >= 3) {
                        String portInfo = parts[0];
                        String service = parts[2];

                        int port = Integer.parseInt(portInfo.split("/")[0]);
                        openPorts.put(port, service);
                    }
                }
            }

            process.waitFor();

        } catch (Exception e) {
            System.err.println("Nmap scan failed: " + e.getMessage());
            // Fallback to basic Java socket testing
            openPorts.putAll(basicPortScan(host));
        }

        return openPorts;
    }

    private Map<Integer, String> basicPortScan(String host) {
        Map<Integer, String> openPorts = new HashMap<>();
        int[] commonPorts = {21, 22, 23, 25, 53, 80, 110, 143, 443, 993, 995, 1433, 3306, 5432};

        for (int port : commonPorts) {
            try (Socket socket = new Socket()) {
                socket.connect(new InetSocketAddress(host, port), 3000);
                openPorts.put(port, "unknown");
            } catch (IOException e) {
                // Port is closed
            }
        }

        return openPorts;
    }

    private void testSSLTLSConfiguration() {
        String httpsEndpoint = "https://application.example.com";

        try {
            // Test SSL/TLS using testssl.sh or similar
            ProcessBuilder pb = new ProcessBuilder(
                "testssl.sh", 
                "--jsonfile", "ssl-report.json",
                "--quiet",
                httpsEndpoint
            );

            Process process = pb.start();
            process.waitFor(60, TimeUnit.SECONDS);

            // Parse SSL test results
            if (new File("ssl-report.json").exists()) {
                ObjectMapper mapper = new ObjectMapper();
                JsonNode sslReport = mapper.readTree(new File("ssl-report.json"));

                // Check for critical SSL issues
                for (JsonNode finding : sslReport) {
                    String severity = finding.get("severity").asText();
                    String finding_text = finding.get("finding").asText();

                    if ("CRITICAL".equals(severity) || "HIGH".equals(severity)) {
                        fail("SSL/TLS security issue: " + finding_text);
                    }
                }
            }

        } catch (Exception e) {
            System.err.println("SSL/TLS test failed: " + e.getMessage());
            // Fallback to basic SSL validation
            validateBasicSSL(httpsEndpoint);
        }
    }

    private void validateBasicSSL(String httpsUrl) {
        try {
            URL url = new URL(httpsUrl);
            HttpsURLConnection connection = (HttpsURLConnection) url.openConnection();

            connection.setRequestMethod("GET");
            connection.setConnectTimeout(5000);
            connection.setReadTimeout(5000);

            // Get SSL certificate
            Certificate[] certificates = connection.getServerCertificates();

            if (certificates.length > 0 && certificates[0] instanceof X509Certificate) {
                X509Certificate cert = (X509Certificate) certificates[0];

                // Check certificate validity
                cert.checkValidity();

                // Check certificate expiration
                Date expirationDate = cert.getNotAfter();
                Date now = new Date();

                long daysUntilExpiration = (expirationDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24);

                assertTrue("Certificate expires within 30 days", daysUntilExpiration > 30);

                // Check key size
                PublicKey publicKey = cert.getPublicKey();
                if (publicKey instanceof RSAPublicKey) {
                    RSAPublicKey rsaKey = (RSAPublicKey) publicKey;
                    int keySize = rsaKey.getModulus().bitLength();
                    assertTrue("RSA key size should be at least 2048 bits", keySize >= 2048);
                }
            }

            // Check TLS version
            String protocol = connection.getCipherSuite();
            assertFalse("TLS version should not be SSLv3", protocol.contains("SSLv3"));

        } catch (Exception e) {
            fail("SSL/TLS validation failed: " + e.getMessage());
        }
    }

