    private void testDockerImageVulnerabilities() {
        String imageName = "myapp:latest";

        try {
            // Scan Docker image for vulnerabilities using Trivy
            ProcessBuilder pb = new ProcessBuilder(
                "trivy", "image", 
                "--format", "json",
                "--output", "image-scan-results.json",
                imageName
            );

            Process process = pb.start();
            process.waitFor();

            // Parse vulnerability scan results
            if (new File("image-scan-results.json").exists()) {
                ObjectMapper mapper = new ObjectMapper();
                JsonNode scanResults = mapper.readTree(new File("image-scan-results.json"));

                int criticalCount = 0;
                int highCount = 0;

                JsonNode results = scanResults.get("Results");
                if (results != null && results.isArray()) {
                    for (JsonNode result : results) {
                        JsonNode vulnerabilities = result.get("Vulnerabilities");
                        if (vulnerabilities != null && vulnerabilities.isArray()) {
                            for (JsonNode vuln : vulnerabilities) {
                                String severity = vuln.get("Severity").asText();

                                switch (severity) {
                                    case "CRITICAL":
                                        criticalCount++;
                                        break;
                                    case "HIGH":
                                        highCount++;
                                        break;
                                }
                            }
                        }
                    }
                }

                System.out.println("Critical vulnerabilities: " + criticalCount);
                System.out.println("High vulnerabilities: " + highCount);

                // Assert acceptable vulnerability levels
                assertEquals("No critical vulnerabilities allowed", 0, criticalCount);
                assertTrue("High vulnerabilities should be minimal", highCount < 5);
            }

        } catch (Exception e) {
            System.err.println("Image vulnerability scan failed: " + e.getMessage());
        }
    }

