    private void testS3BucketSecurity() {
        try {
            ProcessBuilder pb = new ProcessBuilder(
                "aws", "s3api", "list-buckets", "--output", "json"
            );

            Process process = pb.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            StringBuilder output = new StringBuilder();
            String line;

            while ((line = reader.readLine()) != null) {
                output.append(line);
            }

            ObjectMapper mapper = new ObjectMapper();
            JsonNode buckets = mapper.readTree(output.toString());

            JsonNode bucketList = buckets.get("Buckets");
            if (bucketList != null && bucketList.isArray()) {
                for (JsonNode bucket : bucketList) {
                    String bucketName = bucket.get("Name").asText();

                    // Check bucket public access settings
                    ProcessBuilder bucketPb = new ProcessBuilder(
                        "aws", "s3api", "get-public-access-block",
                        "--bucket", bucketName,
                        "--output", "json"
                    );

                    Process bucketProcess = bucketPb.start();
                    BufferedReader bucketReader = new BufferedReader(new InputStreamReader(bucketProcess.getInputStream()));
                    StringBuilder bucketOutput = new StringBuilder();
                    String bucketLine;

                    while ((bucketLine = bucketReader.readLine()) != null) {
                        bucketOutput.append(bucketLine);
                    }

                    if (bucketProcess.waitFor() == 0) {
                        JsonNode publicAccessBlock = mapper.readTree(bucketOutput.toString());
                        JsonNode config = publicAccessBlock.get("PublicAccessBlockConfiguration");

                        if (config != null) {
                            boolean blockPublicAcls = config.get("BlockPublicAcls").asBoolean();
                            boolean blockPublicPolicy = config.get("BlockPublicPolicy").asBoolean();

                            assertTrue("S3 bucket " + bucketName + " should block public ACLs", blockPublicAcls);
                            assertTrue("S3 bucket " + bucketName + " should block public policies", blockPublicPolicy);
                        }
                    }
                }
            }

        } catch (Exception e) {
            System.err.println("S3 security test failed: " + e.getMessage());
        }
    }

    private void testSecurityGroups() {
        try {
            ProcessBuilder pb = new ProcessBuilder(
                "aws", "ec2", "describe-security-groups", "--output", "json"
            );

            Process process = pb.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            StringBuilder output = new StringBuilder();
            String line;

            while ((line = reader.readLine()) != null) {
                output.append(line);
            }

            ObjectMapper mapper = new ObjectMapper();
            JsonNode securityGroups = mapper.readTree(output.toString());

            JsonNode groups = securityGroups.get("SecurityGroups");
            if (groups != null && groups.isArray()) {
                for (JsonNode group : groups) {
                    JsonNode ingressRules = group.get("IpPermissions");

                    if (ingressRules != null && ingressRules.isArray()) {
                        for (JsonNode rule : ingressRules) {
                            JsonNode ipRanges = rule.get("IpRanges");

                            if (ipRanges != null && ipRanges.isArray()) {
                                for (JsonNode range : ipRanges) {
                                    String cidr = range.get("CidrIp").asText();

                                    // Check for overly permissive rules
                                    if ("0.0.0.0/0".equals(cidr)) {
                                        int fromPort = rule.get("FromPort").asInt();
                                        int toPort = rule.get("ToPort").asInt();

                                        // Allow only HTTP/HTTPS from anywhere
                                        if (fromPort != 80 && fromPort != 443 && toPort != 80 && toPort != 443) {
                                            fail("Security group allows access from anywhere (0.0.0.0/0) on non-standard ports: " + fromPort + "-" + toPort);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }

        } catch (Exception e) {
            System.err.println("Security groups test failed: " + e.getMessage());
